css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rutor.info") {
    /* Название раздачи */
    #index tr > td {
      font-size: 16px !important;
      lighting-color: 18px !important;
    }
    
    /* Комментарии */
    td.c_t, td.c_t_a {font-size: 14px !important;}
    tr.c_h b {font-size: 14px !important;}
    
    /* Описание раздачи */
    #details * {font-size: 15px !important;} 
    
    /* Высота меню */
    #up { height: 125px !important; }
    #sidebar { top: 0 !important;}
    
    /* Ширина списка раздач */
    #content {right: 0 !important;}
    
    /* Заголовки в одну строку */
    #index > h2 {float: left !important;}
    
    /* Реклама */
    #flash_header, div.sideblock2 {display: none !important;}
    
}
