css:
@-moz-document url-prefix("https://mail.google.com/") {
/* Button color and styling */
.T-I-KE {
    background-color: #386507 !important;
    background-image: none;
}
.T-I-KE:active {
    background-color: #386507 !important;
    background-image: none;
}
.T-I-KE.T-I-JW {
    background-color: #386507 !important;
    background-image: none;
    border: 1px solid #386507;
}
.T-I-KE {
    color: white;
}
/* Email select color */
.x7 {
    background: #b3f0ff;
}
/* Side color change */
.ain {
    border-left: 4px solid #386507;
}
/* Notice color */
.yb {
	background-color: #ffffe5;
}
.ul {
    background-color: #ffffe5;
}
/* Custom star */
.T-KT-Jp:before {
    content: url(http://puu.sh/mufjh.png);
    content: -webkit-image-set(url(http://puu.sh/mufjh.png) 1x, url(http://puu.sh/mufdu.png) 2x);
}
.T-KT-JW:before {
    content: url(http://puu.sh/mufjh.png) !important;
    content: -webkit-image-set(url(http://puu.sh/mufjh.png) 1x, url(http://puu.sh/mufdu.png) 2x);
}
/* Box for silly animation */
.gb_ea.gb_Vc.gb_R {
    width: 30px;
    height: 30px;
    border: 1px solid grey;
}
/* Little square for animation */
.gb_b.gb_Nb {
    position: relative;
    background-color: red;
    width: 12px;
    height: 12px;
    background-image: none;
    -webkit-animation: slidey 7s linear 2s infinite;
}
/* Stuff for lemur psuedo-zoom */
.gb_Ob {
    -webkit-transform-origin: left top;
    -webkit-transition-property: left;
    -webkit-transition-duration: 1s;
    -webkit-transition-timing-function: ease-out;
    -webkit-transition-delay: 0;
	transition: all .2s ease-in-out; 
    background-image: url('http://puu.sh/mulGH.png') !important;
}
.gb_Ob:hover {
    -webkit-animation-play-state: paused;
    -moz-animation-play-state: paused;
    -o-animation-play-state: paused;
    animation-play-state: paused;
    transform: scale(11);
}
.gb_Ib {
    overflow: visible;
}
.gb_Ob {
	background-image: url('http://puu.sh/mulGH.png') !important;
    position:relative;
    -webkit-animation: lemur 0.1s infinite;
}

/*[[speed]]*/

/* Silly sliding animation */
@keyframes slidey {
    0%, 100%  		    {background-color:red;      left:8.5px; top:-3px;}
    5%   		     	{background-color:orange;   left:19px;  top:-3px;}
    10% 		        {background-color:yellow;   left:19px;  top:16px;}
    15% 			    {background-color:green;    left:0px;   top:16px;}
    20%			        {background-color:blue;     left:0px;   top:-3px;}
    25%      			{background-color:indigo;   left:8.5px; top:-3px;}
    30%, 40% 			{background-color:purple;   left:8.5px; top:6px;}
    50%, 75%, 86.25%    {background-color:red;      left:0px;   top:16px;}
    55%, 77.5%, 87.5%   {background-color:orange;   left:8.5px; top:-3px;}
    60%, 80%, 88.75%    {background-color:yellow;   left:19px;  top:16px;}
    65%, 82.5%, 90%     {background-color:green;    left:0px;   top:4px;}
    70%, 85%, 91.25%    {background-color:blue;     left:19px;  top:4px;}
    92.5%    			{background-color:green;    left:8.5px; top:-3px;}
}

@-webkit-keyframes slidey {
    0%, 100%  		    {background-color:red;      left:8.5px; top:-3px;}
    5%   		     	{background-color:orange;   left:19px;  top:-3px;}
    10% 		        {background-color:yellow;   left:19px;  top:16px;}
    15% 			    {background-color:green;    left:0px;   top:16px;}
    20%			        {background-color:blue;     left:0px;   top:-3px;}
    25%      			{background-color:indigo;   left:8.5px; top:-3px;}
    30%, 40% 			{background-color:purple;   left:8.5px; top:6px;}
    50%, 75%, 86.25%    {background-color:red;      left:0px;   top:16px;}
    55%, 77.5%, 87.5%   {background-color:orange;   left:8.5px; top:-3px;}
    60%, 80%, 88.75%    {background-color:yellow;   left:19px;  top:16px;}
    65%, 82.5%, 90%     {background-color:green;    left:0px;   top:4px;}
    70%, 85%, 91.25%    {background-color:blue;     left:19px;  top:4px;}
    92.5%    			{background-color:green;    left:8.5px; top:-3px;}
}

}
