css:
@-moz-document domain("wikipedia.org") {
  
#mw-head, #mw-panel, #p-logo, .mw-content-ltr div.magnify a {
  opacity: 0 !important;
}
  
#mw-head:hover, #mw-panel:hover,#p-logo:hover, .mw-content-ltr div.magnify a:hover {
  opacity: 1 !important;
}  
  
.mw-body {
  width: 52% !important;
  border: 1px solid #1a1b26 !important;
  margin: 0 auto !important;
}  
  
.mw-body, div#simpleSearch #searchInput, div#simpleSearch #searchButton, div#simpleSearch #mw-searchButton {
  background-color: #1d1e2a !important;
  color: #8a7b70 !important;
}  
  
a, table.wikitable > * > tr > td {
   color: #556b82 !important;
}  
  
a:hover, a:focus, h1, #toc h2, .toc h2 , .mw-body h2, h3, h4, #collapsibleTable0 > caption, table.infobox, table.wikitable {
  color: #74857c !important;
}  
  
h1, #mw-content-text > h2 {
  border-bottom: 1px solid #14151f !important;
}  
  
body, #mw-page-base, div.vectorTabs li, div.vectorTabs, div.thumbinner, div.thumb, #toc, .toc, .mw-warning, .toccolours, .infobox, table.wikitable, table.wikitable > * > tr > td, table.wikitable > * > tr > th, #mp-topbanner, #mp-upper {
  background: #14151f !important;
}  
  
div.thumbinner {
  border: 2px solid #14151f !important;
}  

div.vectorTabs, #simpleSearch, html .thumbimage, #toc, .toc, .mw-warning, .toccolours, .infobox, #mp-topbanner {
  border: none !important;
}  
  
table.wikitable > * > tr > td, table.wikitable > * > tr > th {
  border: 2px solid #1d1e2a !important;
}  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
}
