css:
@-moz-document domain(instagram.com) {
    /* Background, border & text colours */
    body, section, main, article, header, footer,
    section > main > article > div,
    section > main > div > ul,
    section > main > div > ul > li,
    section > main > section > div > div > article > div > div,
    section > main > section > div > div > article > div > div > div,
    section > nav,
    section > nav > div > div,
    section > nav > div > div > div > div > input,
    section > nav > div > div > div > div > div,
    section > nav > div > div > div > div > div > div > span,
    section > nav > div > div > div > div > div > div > div > a,
    section > nav > div > div > div > div > div > div > div > div,
    section > nav > div > div > div > div > div > div > div > div > ul > li {
        background-color: #000000 !important;
        border-color: #151515 !important;
        color: rgba(255,255,255,0.3) !important;
    }

    /* Hide Suggestions for You, and other useless elements */
    span > section > main > section > ul,
    section > nav > div > div > div > div {
        display: none;
    }

    /* Buttons */
    section > main > article > header > div > div > span > button,
    section > main > div > ul > li > div > div > span > button,
    section > main > article > header > div > div > span > button,
    section > main > section > div > div > article > div > ul > li > span > a,
    section > main > section > div > div > article > div > ul > li > h1 > span > a,
    section > nav > div > div > div > div > div > div > div > div > ul > li > span > span > a {
        background-color: #151515 !important;
        border-color: #999999 !important;
        color: #999999 !important;
    }

    /* User Profile Names */
    section > main > section > div > div > article > header > div > a,
    footer > div > nav > ul > li > a,
    footer > div > nav > ul > li > span {
        color: #525252 !important;
    }

    section > nav > div > div > div > div > div > div > div > a:hover,
    section > nav > div > div > div > div > div > div > div > a:active,
    section > nav > div > div > div > div > div > div > div > a:focus {
        background-color: #080808 !important;
    }

    /* Removes Right Click Shield */
    section > main > section > div > div > article > div > div > div,
    section > main > section > div > div > article > div > div > div > div {
        display: none;
    }

    /* Input Fields */
    section > nav > div > div > div > div > input,
    div > div > div > div > article > div > section > form > input,
    section > main > section > div > div > article > div > ul > li > span,
    section > main > section > div > div > article > div > section > form > input {
        color: rgba(255,255,255,0.3) !important;
    }
}
