css:
/*!========================================\\
||           ReadManga.Tonight             ||  Author: Alexander Gremory
||~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~||  Version: 2.0
||         A simple customizable theme     ||  
||         stylebase.xyz                   ||  
\\~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~!*/
@-moz-document domain("readmanga.today") {
.col-left .content-list {background:none;background-color:/*[[grey]]*/!important}
.col-left .col-top, div.row,.col-right{background:none;background-color:/*[[dgrey]]*/!important;box-shadow:none!important}
.header .social-login>ul>li.search form .tt-query{background:/*[[dgrey]]*/!important;box-shadow:none!important}
.manga_updates dd a{color:/*[[ee1]]*/!important}
p {color:/*[[eae]]*/!important}
.col-right .similar-movies ul li .movie-title {color:#bbb!important}
.col-right .category-list .main-categories>li .sub-categories>li>a:hover {background-color:/*[[lgrey]]*/;color:/*[[ee1]]*/}
.col-right .category-list .main-categories>li .sub-categories>li>a {color:#bbb}
body{color:black}
.col-right .category-list .main-categories>li .sub-categories>li>a .counter {text-shadow:none;color:/*[[ee1]]*/;background:none;background-color:/*[[dgrey]]*/}
.col-right .category-list .main-categories>li .sub-categories>li>a .counter:hover {background-color:/*[[lgrey]]*/!important}
.col-right .category-list .main-categories>li>a,.col-right .category-list .main-categories>li>a span,.col-right .title,.col-right .title span,.header .navigation ul>li.home {background:none;background-color:/*[[dgrey]]*/!important}
.col-right .category-list .main-categories>li .sub-categories>li>a {background:none!important;background-color:/*[[grey]]*/!important;}
.btn,.col-left .col-top .list-switcher li a{border:0px!important;background:none!important;background-color:/*[[lgrey]]*/!important}
.btn:hover,.col-left .col-top .list-switcher li a:hover{background:none!important;background-color:/*[[grey]]*/!important}
.alert-info{border-color:/*[[dgrey]]*/;background:none;background-color:transparent}
.col-right .latest-comments>ul>li .comment {background-color:/*[[dgrey]]*/}
.col-right .latest-comments>ul>li .comment>a {color:/*[[eae]]*/}
.footer .inner .sitemap li a {color:/*[[blue]]*/}
.col-right .latest-comments>ul>li .meta,.col-right .latest-comments>ul>li .movie-title {color:#d34836;background-color:/*[[grey]]*/}
.footer .inner {background:none;background-color:/*[[lgrey]]*/;color:/*[[fff]]*/!important}
.col-single .content-list{background-color:/*[[dgrey]]*/}
.panel {background:/*[[lgrey]]*/}
.btn {border-radius:0px!important}
.col-single .movie-meta .dl-horizontal dd {background-color:/*[[lgrey]]*/}
.col-single .movie-meta .panel .panel-heading {background-color:/*[[dgrey]]*/}
.list-group-item:first-child,.panel>.list-group .list-group-item, .panel>.list-group .list-group-item:first-child {background-color:/*[[lgrey]]*/}
.form-control {background-color:/*[[lgrey]]*/!important}
div.panel-heading {background-color:/*[[dgrey]]*/!important}
.col-single .cast .panel .panel-heading{color:/*[[fff]]*/!important}
.dropdown-menu{background-color:/*[[dgrey]]*/!important}
.dropdown-menu li a {color:/*[[fff]]*/!important}
.manga-item a{color:/*[[blue]]*/!important}
.manga-list .manga-letters a {background-color:/*[[dgrey]]*/}
.manga-list .manga-letters a.active {background-color:/*[[dgrey]]*/}
.col-left .content-list .box,.hot-manga .box{border-radius:0px;border:1px;border:hidden!important;background-color:transparent}
.header .navigation .tools>ul>li {background:none;border-right:/*[[lgrey]]*/}
.modal-content {background-color:/*[[grey]]*/}
.form-control {color:black}
.btn, .col-left .col-top .list-switcher li a {background-color:#585858!important}
.col-left .content-list .box .title,.col-left .content-list .box .meta {background-color:/*[[dgrey]]*/;border-top-left-radius:0px!important;border-top-right-radius:0px!important;border-bottom-left-radius:0px!important;border-bottom-right-radius:0px!important}
.header .navigation .tools-dropdown, .header .navigation .login-dropdown {background-color:/*[[dgrey]]*/}
.col-left .content-list .style-thumbnail ul li a.title {background-color:/*[[dgrey]]*/}
div.title,.meta,.hot-manga .meta {background-color:/*[[dgrey]]*/!important}
div.body {background-color:/*[[grey]]*/!important}
span.icon{background:none!important}
.hot-manga-dl-horizontal dd, .hot-manga-dl-horizontal dd a {color:/*[[fff]]*/;background-color:/*[[dgrey]]*/!important}
.col-single .movie-meta .panel .list-group ul li,.col-single .movie-meta .panel .list-group ul li.facebook,.col-single .movie-meta .panel .list-group ul li.twitter {background-color:/*[[dgrey]]*/!important}
.col-single .movie-meta .panel .list-group ul li a {color:/*[[fff]]*/}
.col-single .movie-meta .panel .list-group ul li span {background-color:/*[[dgrey]]*/!important}

.content .lol-left {background:none;background-color:/*[[grey]]*/!important}
.content .lol-left a {color:/*[[eae]]*/!important}
.content .lol-left a:hover {color:/*[[ee1]]*/!important;background-color:/*[[lgrey]]*/!important;border:0px}
.content .nav-tabs li.active a {color:/*[[ee1]]*/!important;background:none;background-color:/*[[lgrey]]*/!important;border:0px!important}
.content .nav-tabs li.active a:hover {background-color:/*[[lgrey]]*/!important}
.content .lol-right {background:none;background-color:/*[[grey]]*/!important}
.content .lol-right a {color:/*[[eae]]*/!important}
.content .movies>ul>li .movie-title{background:none;background-color:/*[[dgrey]]*/!important}
.content .lol-right .content .movies>ul>li .comment {background:none;background-color:/*[[grey]]*/!important;padding:0px}
.content .lol-right .thumbnail {background:none!important;background-color:/*[[dgrey]]*/!important;border:0px;border-radius:0px;}
.pagination>li>a, .pagination>li>span{border:1px;border-color:/*[[dgrey]]*/;background-color:/*[[lgrey]]*/}
.pagination>li>a:hover {background-color:/*[[lgrey]]*/}
.pagination>li>a, .pagination>li>span:focus {border:1px;border-color:/*[[dgrey]]*/;background-color:/*[[grey]]*/}
.col-left .content-list .pagination>li.active a{border:1px;border-color:/*[[dgrey]]*/;background-color:/*[[dgrey]]*/}
.slider .slider-container a.slider-next, .slider .slider-container a.slider-prev {background-color:/*[[dgrey]]*/;color:#ff1}
.progress {background-color:/*[[dgrey]]*/}
.slider .progress-wrapper .progress .progress-bar-danger {background-color:/*[[grey]]*/}
.slider .slider-container .slides ul li {background-color:/*[[dgrey]]*/}
.slider .slider-container .slides ul li a.title {background-color:/*[[dgrey]]*/}
.col-left .content-list .box .title span {background-color:transparent}
.col-left .content-list .box .title span[class^="icon-"]{background-color:transparent}
}
