css:
@-moz-document
regexp("(https|http)://(search.|www.|)disconnect.me/.*")
{
  body, div#results #normal-results.image-results
  {
    font-family: sans-serif !important;
    background: url("/*[[img]]*/") /*[[clr]]*/ bottom fixed no-repeat !important;
    background-size: cover !important;
    color: #FAFAFA !important;
  }
  
  div[id="premium-protection"]
  {
    /*display: none !important;*/ /*If you want to disable that pesky ad*/
  }
  
  #privacy h3
  {
    color: #FAFAFA !important;
  }
  
  header
  {
    background: #161616 !important;
    border-bottom: 2px solid #000 !important;
    box-shadow: 0px 1px 1px #0A0A0A !important;
  }
  
  #products h1, #products h2, #products h3, #products h4, h1
  {
    color: #47a57f !important;
  }
  
  #products td.product-free button, button#atfcta2
  {
    background: #1C1C1C !important;
    color: #CCCCCC !important;
  }
  
  #products td.product-free button:hover, button#atfcta2:hover
  {
    background: #161616 !important;
    color: #FAFAFA !important;
  }
  
  #products td.product-pro button:hover
  {
    opacity: 1 !important;
  }
  
  #hdtb_msb
  {
    background: #1C1C1C !important;
  }
  
  .dropdown_wrapper .dropdown_content ul li
  {
    background-color: #161616 !important;
  }
  
  #location
  {
    background: #161616 !important;
    color: #FAFAFA !important;
  }
  
  .top-form-input.home
  {
    border: none !important;
  }
  
  #main-search #home_search
  {
    background-color: #161616 !important;
    border: solid #3B8C6D 1px !important;
  }
  
  header fieldset .search_box, #main-search input[type="text"]
  {
    background: #3C3C3C !important;
    color: #FAFAFA !important;
    border: solid #0A0A0A 1px !important;
    box-shadow: none !important;
  }
  
  header fieldset .search_box:focus, #main-search input[type="text"]:focus
  {
    /*border: solid #0973BF 1px !important;*/ /*I'm not sure if this looks good*/
    box-shadow: none !important;
  }
  
  .dropdown_wrapper.home .dropdown_content ul li
  {
    color: #CCCCCC !important;
  }
  
  .dropdown_wrapper.home .dropdown_content ul li.active, .dropdown_wrapper.home .dropdown_content ul li:hover
  {
    color: #FAFAFA !important;
  }
  
  #normal-results li .url
  {
    color: #0973BF !important;
  }
  
  div#results #normal-results li
  {
    color: #CCCCCC !important;
  }
  
  #normal-results a
  {
    color: #FAFAFA !important;
    text-decoration-line: none !important;
  }
  
  #normal-results a:visited
  {
    color: #CCCCCC !important;
  }
  
  #normal-results a:hover
  {
    color: #FAFAFA !important;
    text-decoration-line: underline !important;
  }
  
  .pagination a
  {
    color: #CCCCCC !important;
  }
  
  .pagination a:hover
  {
    color: #CCCCCC !important;
    text-decoration-line: underline !important;
  }
  
  #cookie_compliance
  {
    background: #131313 !important;
  }
  
  #cookie_compliance button.outline
  {
    background: #131313 !important;
    color: #CCCCCC !important;
  }
  
  #cookie_compliance button.outline:hover
  {
    background: #0A0A0A !important;
    color: #FAFAFA !important;
  }
  
  #cookie_compliance #cc_info
  {
    background: #0A0A0A !important;
  }
  
  #cookie_compliance a, body a
  {
    color: #0973BF !important;
  }
  
  .hdtb_mitem, div[style="font-family: Avenir Next Thin; font-size: 17px;color: #6B6B6B; line-height: 0px; position:relative; top:7px; left:3px;"], #protect .sub-header, #search-top a.download
  {
    color: #FAFAFA !important;
  }
  
  .hdtb_mitem a
  {
    background: #1C1C1C !important;
    color: #CCCCCC !important;
  }
  
  .hdtb_mitem a:hover
  {
    background: #1C1C1C !important;
    color: #FAFAFA !important;
  }
  
  .ui-menu
  {
    background: transparent linear-gradient(#3C3C3C, #2E2E2E) repeat scroll 0% 0% !important;
    border: none !important;
  }
  
  .ui-menu .ui-menu-item a
  {
    background: transparent !important;
    color: #FAFAFA !important;
  }
  
  .ui-menu .ui-menu-item a:hover, .ui-menu .ui-menu-item a:focus, .ui-menu .ui-menu-item a:active
  {
    background: transparent linear-gradient(#3C3C3C, #2E2E2E) repeat scroll 0% 0% !important;
    color: #FAFAFA !important;
  }
  
  #internet-access
  {
    background: #1C1C1C !important;
  }
  
  #products, #performance .slant.grey, #privacy-policy, #products .available-for, #protect
  {
    background: transparent !important;
  }
  
  #products > div button, #products > div a.button
  {
    color: #FAFAFA !important;
  }
  
  #search-top #browser
  {
    color: #0A0A0A !important;
  }
}
