css:
@-moz-document domain("tvtropes.org") {

#main-content-sidebar {
width: 185px !important;
min-width: 185px !important;
padding-left: 18px !important;
padding-right: 18px !important;
background: #EFEFEF none repeat scroll 0% 0% !important;
font-size: 0.8em !important;}

#main-entry.with-sidebar{
max-width:calc(100% - 36px) !important;}
  
   a.twikilink:link, a.twikilink:visited{
    color: #191970 !important;
   }
   
   a.urllink:link, a.urllink:visited {
    color: #191970 !important;
   }

   a.createlink:link, a.createlink:hover, a.createlink:visited {
    color: #C11D1D !important;
   }
   
   a:hover {
     background:none !important;
   }

   ul.selector.is-off a:hover{
     background:#9C9FA4 none repeat scroll 0% 0% !important;
   } 
   
   ul.selector li.on a:hover{
     background:#9DC000 none repeat scroll 0% 0% !important;
   } 
   
   .caption a:link, .caption a:visited{
    color: #191970 !important;
   }
   
   .caption p{
      color: #000 !important;
   }
   div.list-inline > h3:nth-child(1){
   margin:0 80px 0 0 !important;
   padding: 6px 15px !important;
   }

iframe:nth-child(1) > div:nth-child(2) {width:151px !important;}

iframe:nth-child(1){width:151px !important;}

#google_ads_iframe_\/5376056\/tvtropes_side_1_0__container__ {width:175px !important;}

#action-bar-top{display:inherit !important;}

#main-content-sidebar > div:nth-child(3) > div:nth-child(1) {width:151px !important;}

#main-content-sidebar > div:nth-child(3) > div:nth-child(1) > div:nth-child(1) {width:151px !important;}

#main-content-sidebar > div:nth-child(3) > div:nth-child(1) > div:nth-child(2) {width:145px !important;}

}
