css:
@-moz-document url-prefix("http://www.roblox.com"), url-prefix("https://www.roblox.com"), url-prefix("http://de.roblox.com"), url-prefix("https://de.roblox.com"), url-prefix("https://forums.roblox.com"), url-prefix("http://web.roblox.com"), url-prefix("https://web.roblox.com"), url-prefix("http://forums.roblox.com"), domain("roblox.com") {
/*ROBLOX OS X/iOS-Like Blur Theme by harule - https://www.roblox.com/users/17277708/profile*/

/*This adds a small blur effect to the side navigation bar, and some other elements like dialogs and the chat feature. Similar to OS X >= Yosemite/iOS >= 7*/

/*Also for some reason, Stylish thinks that "backdrop-filter" dosen't exist and displays that as an error, just ignore that... :P*/


/*.rbx-header:{
    background-color: rgba(0,116,189,0.75);
    backdrop-filter: blur(20px) !important;
    -webkit-backdrop-filter: blur(20px) !important;
}NOTE: Kind of broken, looks weird if you open any dropdown on the navbar.*/

#navigation {
    background-color: rgba(242, 242, 242, 0.75);
    backdrop-filter: blur(20px) !important;
    -webkit-backdrop-filter: blur(20px) !important;
    box-shadow: 0 0 1px rgba(25, 25, 25, 0.5) !important;
}

.dialog-body {
    background-color: rgba(227, 227, 227, 0);
    backdrop-filter: blur(0px) !important;
    -webkit-backdrop-filter: blur(0px) !important;
}

.dialog-container {
    background-color: rgba(227, 227, 227, 0);
    backdrop-filter: blur(20px) !important;
    -webkit-backdrop-filter: blur(20px) !important;
    box-shadow: 0 0 1px rgba(25, 25, 25, 0.5) !important;
}

.dialog-container .dialog-body {
    background-color: rgba(227, 227, 227, 0.75) !important;
}

.dialog-message {
    box-shadow: 1px 1px 0px rgba(25, 25, 25, 0.2) !important;
    -webkit-box-shadow: 1px 1px 0px rgba(25, 25, 25, 0.2) !important;
}

.dialog-container .dialog-report-container {
    background-color: rgba(227, 227, 227, 0.75);
    backdrop-filter: blur(5px) !important;
    -webkit-backdrop-filter: blur(5px) !important;
}

.simplemodal-overlay {
    background-color: rgb(0, 0, 0);
    backdrop-filter: blur(10px) !important;
    -webkit-backdrop-filter: blur(10px) !important;
}

.modal-backdrop {
    background-color: rgb(0, 0, 0);
    backdrop-filter: blur(10px) !important;
    -webkit-backdrop-filter: blur(10px) !important;
    transition: transform .3s ease-out;
    -webkit-transition: -webkit-transform .3s ease-out;
}

.chat-friend-list .chat-friend-info .chat-friend-message {
    background-color: rgba(242, 242, 242, 0) !important;
}

.chat-friend-list .chat-friend-info .chat-friend-message:after {
    background-image: none !important;
}

#chat-main {
    background-color: rgba(242, 242, 242, 0.75) !important;
    backdrop-filter: blur(20px) !important;
    -webkit-backdrop-filter: blur(20px) !important;
    box-shadow: 0 0 1px rgba(25, 25, 25, 0.75) !important;
}

.chat-friend-list .chat-friends {
    background-color: rgba(255, 255, 255, 0.75) !important;
}

.scroller:hover {
    box-shadow: none !important;
    background-color: rgba(255, 255, 255, 0.9) !important;
}

.scroller {
    background-color: rgba(255, 255, 255, 0.75) !important;
    backdrop-filter: blur(20px) !important;
    -webkit-backdrop-filter: blur(20px) !important;
    box-shadow: none !important;
    opacity: 1 !important;
}

.dropdown-menu {
    background-color: rgba(255, 255, 255, 0.75) !important;
    backdrop-filter: blur(20px) !important;
    -webkit-backdrop-filter: blur(20px) !important;
    box-shadow: none !important;
}

.dropdown-menu li:hover {
    background-color: rgba(255, 255, 255, 0.8) !important;
    backdrop-filter: blur(20px) !important;
    -webkit-backdrop-filter: blur(20px) !important;
}

.roblox-popover-content.bottom .notification-stream-wrap.open {
    background-color: rgba(255, 255, 255, 0.75);
    backdrop-filter: blur(20px) !important;
    -webkit-backdrop-filter: blur(20px) !important;
    box-shadow: 0 0 1px rgba(25, 25, 25, 0.5) !important;
}

.notification-stream-body .container-empty, .notification-stream-body {
    background-color: rgba(255, 255, 255, 0);
}

.roblox-popover-content.bottom .notification-stream-wrap {
	top: 30px;
}

.roblox-popover-content.bottom .notification-stream-wrap.open {
	top: 40px;
}

.roblox-popover-content.bottom .arrow {
	display: none;   
}

.notification-stream-banner.on {
    display: block !important;
}

.notification-stream-banner.banner-error, .notification-stream-banner.banner-new {
	display: none;   
}

.notification-stream-header {
	background-color: rgba(255, 255, 255, 0.35) !important;   
}
}

@-moz-document url("https://www.roblox.com/my/avatar"), url("http://www.roblox.com/my/avatar") {
/*Blur stuff for the avatar page*/

.items-list .item-card .item-card-menu {
    background-color: rgba(255, 255, 255, 0.75) !important;
    backdrop-filter: blur(5px) !important;
    -webkit-backdrop-filter: blur(5px) !important;
}

.section-content {
    background-color: rgba(255, 255, 255, 0.75) !important;
    backdrop-filter: blur(20px) !important;
    -webkit-backdrop-filter: blur(20px) !important;
    box-shadow: 0 0 1px rgba(25, 25, 25, 0.75) !important;
}
}
