css:
@-moz-document domain("tvtropes.org") {
/*black background*/
.main-full-width,
.main-left.inner,
.inner,
.main-left,
#header-new,
.acaptionright,
.section-link-row,
.trope-body,
.tab-content
{ background: black !important; }

body,
h2,
.folderlabel,
.form-control,
.gsc-control-cse,
.btn-default,
.footer-top,
.footer-bottom,
.article-page .page-content h1
{ background-color: black !important; }

/*hide dumb stuff*/
.sidebar-right,
.main-body-links,
.secondary-nav
{ display: none; }

/*fonts*/
h2,
.folderlabel,
body,
span
{ color: #666 !important; }

/*link font*/
a
{ color: grey !important; }

.gs-title:hover
{ color: #92badc !important; }

.twikilink:hover
{ color: #07385a !important; }

/*spoilers*/
span.spoiler
{ background-color: grey !important; color: grey !important; border-color: grey !important; }

span.spoiler:hover,
a:hover
{ color: black !important; }
}
