css:
@-moz-document url-prefix("https://vulkan.lunarg.com/doc/"), url-prefix("https://vulkan.lunarg.com/app/docs/") {

article p, article li, article td {
   max-width: 40em;
}

div.col div.padder-lg div.ng-scope p
{
    max-width: 43em;
}

div.col div.padder-lg div.ng-scope ul,
div.col div.padder-lg div.ng-scope ol
{
    max-width: 60em;
}

div.col div.padder-lg div.ng-scope li
{
    max-width: 40em;
}

div.col div.padder-lg div.ng-scope table
{
    max-width: 60em;
}

div.col div.padder-lg div.ng-scope td
{
    max-width: 40em;
}

#app > div.app-aside.hidden-xs.ng-scope > div > div:after
{
    display: block;
    max-width: 80%;
    margin: 0 auto 0 auto;
    content: "Get more examples and helper code at krust.org.";
}
}
