css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("bgmlist.com"){
body{background:/*[[backgroundcolor]]*/;color:/*[[fontcolor]]*/}
.main .setting-btn,.main .setting-mask .setting .check.on,.main .setting-mask .setting .number-selector .number-control-btn{background:/*[[secondarycolor]]*/;box-shadow:1px 1px 2px /*[[shadowcolor2]]*/}
.main .setting-mask .setting,.main .table-container .data-list .data-not-onair .item-main,.main .table-container .data-list .item-main,.main .table-container .data-list .item-main:hover,header{background:/*[[primarycolor]]*/;box-shadow:2px 2px 3px /*[[shadowcolor1]]*/;color:/*[[fontcolor]]*/}
.main .table{border:10px solid /*[[primarycolor]]*/;background:/*[[primarycolor]]*/;box-shadow:2px 2px 3px /*[[shadowcolor1]]*/}
.main .table-container{background:0 0}
.main .table-container .data-list{border-color:/*[[primarycolor]]*/;border-radius:4px;background:/*[[backgroundcolor]]*/;color:/*[[fontcolor]]*/}
.main .table-container .data-list .item-sub,.main .table-container .data-list>ul{border:none;border-radius:4px;background:/*[[backgroundcolor]]*/;color:/*[[fontcolor]]*/}
.main .table-container .data-list li{color:/*[[fontcolor]]*/}
.main .table-container .data-list .item-main{border:1px solid /*[[primarycolor]]*/}
.main .table-container .selector li a{border:1px solid /*[[primarycolor]]*/;border-right:none}
.main .table-container .selector li:hover a{border:1px solid /*[[backgroundcolor]]*/!important;border-right:none!important;background:0 0}
.main .table-container .selector li.cur a{border:1px solid /*[[backgroundcolor]]*/!important;border-right:none!important;background:/*[[backgroundcolor]]*/}
.main .table-container .data-list .item-main .sites{border:none}
.main .table-container .data-list .item-main>.title span:hover,.main .table-container .data-list li a:hover,header .header-nav a:hover{color:/*[[secondarycolor]]*/}
.main .table-container .data-list .new .item-main::before{background:/*[[secondarycolor]]*/}
.main .table-header .sites,.main .table-header .time-cn,.main .table-header .time-jp,.main .table-header .title{color:/*[[fontcolor]]*/}
.main .setting-mask{border-radius:4px;background:/*[[backgroundcolor]]*/;box-shadow:inset 1px 1px 3px /*[[shadowcolor1]]*/}
.main .setting-mask .setting .setting-box{border:none;background:/*[[backgroundcolor]]*/;box-shadow:inset 1px 1px 3px /*[[shadowcolor1]]*/}
.main .setting-mask .setting .setting-confirm{background:/*[[secondarycolor]]*/;box-shadow:1px 1px 2px /*[[shadowcolor2]]*/}
.main .setting-mask .setting .setting-reset{box-shadow:1px 1px 2px /*[[shadowcolor2]]*/}
.main .setting-mask .setting .check,.main .setting-mask .setting .number-selector .number-control-btn.disabled{background:/*[[primarycolor]]*/;box-shadow:1px 1px 2px /*[[shadowcolor2]]*/}
header .drop-menu,header .drop-menu .sub-menu{background:/*[[primarycolor]]*/;box-shadow:2px 2px 3px /*[[shadowcolor1]]*/;color:/*[[fontcolor]]*/}
header .drop-menu::before{top:-15px;border-color:transparent transparent /*[[primarycolor]]*/}
header .drop-menu li:hover a{background:0 0!important;color:/*[[secondarycolor]]*/}
select{background:/*[[shadowcolor1]]*/;border:0;color:/*[[fontcolor]]*/;height:22px;box-shadow:inset 1px 1px 3px /*[[shadowcolor3]]*/}
.top .search-box .searcher input,.top .search-box .searcher input:focus{border:2px solid /*[[secondarycolor]]*/;background:/*[[shadowcolor1]]*/;box-shadow:1px 1px 3px /*[[shadowcolor3]]*/;color:/*[[fontcolor]]*/}
.top .search-box .searcher .search-btn{background-color:/*[[secondarycolor]]*/;background-position:50% 50%;background-size:50% 50%}
}
