css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wap.jjwxc.com"),domain("wap.jjwxc.net") {
   /*全局页面收窄居中*/
   body{
   margin-left:auto;margin-right:auto;width:/*[[width]]*/px;}
   /*全局缩放*/
   body{-moz-transform:scale(/*[[value]]*/);-moz-transform-origin:top center;}
   /*阅读背景色*/
   body{background-color:/*[[color]]*/ !important;}
   /*广告屏蔽*/
   IMG,
   DIV.hd,
   DIV#right,
   DIV[style="position:static"],
   DIV[id$="_center"],
   DIV[style="text-align:center;margin:0 auto;width:100%;"],
   SPAN[style="font-size:12px;color:rgb(128,128,128)"],
   DIV[id="iadA"]
   {display:none!important;}
   /*阅读文字字体大小*/
   body{font-size:18px;}
   DIV[style="padding:1px;font-size:14px;"]{font-size:18px!important;}
   TD[style="width: 40%;"]{font-size:12px!important;}
   LI[style="font-size: 12px; color: #009900;"]{font-size:12px!important;}
    
}
