@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.twitch.tv") {

.chat-rules
{
  z-index: 20 !important;
}
  
/*emoticon and bits icons*/                  
.tw-top-0.tw-right-0.tw-absolute{
  position: absolute!important;
  margin-right: 50px !important;
  padding-top: 12px !important;
  right: 25px !important;
}

/*padding for text input on right side of chat box*/   
.tw-textarea.tw-textarea--no-resize{
  padding-right: 5px !important;
}

/*chat only text box bottom margin*/ 
.scroll.chat-messages.js-chat-messages.hideTimestamps.hideModIcons.showAutoModActions{
  bottom: 30% !important;
}

/*change chat line scroll box main view and VOD*/
.chat-list__lines.scrollable-area{
  margin-bottom: 90px  !important;
}
   
