css:
@namespace url(http://www.w3.org/1999/xhtml);
/* v5 by Rist - https://userstyles.org/users/26599 */
@-moz-document regexp("(|http(|s)://)(|www.)m(|.*.)delfi\.ee.*") {
#md-wrapper{
        max-width: 4000px!important;
    }

/*Article, comment*/
    #article,
    .article,
    #comment-dark-skin-wrapper{
        max-width: 800px!important;
        margin: 0 auto !important;
    }

    .comment-add.comment-add-footer-sticky .comment-add-footer{
        width: 190px!important;
        margin-left: auto!important;
}

/*Main page*/
    .md-mosaic-small,
    .md-mosaic-big,
    .md-mosaic-right,.md-mosaic-small.md-mosaic-right{
    float: left!important;
    box-sizing: border-box!important;
        padding: 1px!important;
        margin: 0px!important;
	width: /*[[mboxwidth]]*/px!important;/*=article box size*/
        clear: none!important;
    }
    .md-block-title {
        margin: 0!important;
    }
    .clear,
    .md-banner-placement {
        clear: none!important;
        float:left!important
    }

/*Font size,heigh,overflow*/
    .md-mosaic-big .md-mosaic-title,
    .md-mosaic-small .md-mosaic-title,
    .md-mosaic-right .md-mosaic-title,
    .md-article-time{
        font-size: 14px !important;
        line-height: 1.1 !important;
        padding: 0 !important;
        height: 70px !important;
        overflow-y: auto !important;
        overflow-x: auto !important;
    }

/*Remove ads,sponsor etc*/
    .md-specialblock-content,
    .md-specialblock-carousel,
    .md-specialblock.md-specialblock-id-ev100peret,
    #stream-schedule,
    .md-banner-placement.md-banner-placement-visible,
    .md-banner-container,
    .md-banner-placement,
    .ab-newsletterForm,
    .ad_amiddle.dado-pl,
    .social-buttons.social-buttons-fixed,
    .dweather-mobile-widget,
    .md-related-articles.cXense,
    .delfi-player.cast--men-dont-cry,
    .stream-container,
    #cx-suggested-articles,
    .md-specialblock-cols-3.md-specialblock-id-special_1.md-specialblock,
	.md-specialblock-cols-3.md-specialblock-id-special_4.md-specialblock,
	.md-specialblock-cols-3.md-specialblock-id-special_extra.md-specialblock,
    .md-specialblock-id-ak-magazines.md-specialblock-outer,
    #dtv-schedule-18{
        display: none!important;
    }

  iframe
  {
    max-height: 450px !important;
    max-width: 800px !important;
  }
}
