css:
@-moz-document regexp("https*://www.google.*") {
.sfbgg, .gb_Xb{
	background-image: none !important;
    background-color: #26A69A !important;
  	box-shadow: 0 2px 5px rgba(0,0,0,0.8);
    border: none !important;
}
.srp #searchform {
	position: fixed;
}
div#logocont, #gbq1 {
  	visibility: hidden !important;
}
.kpbb, #gbqfb{
	-webkit-transition: all 0.218s,visibility 0s;
	transition: all 0.218s,visibility 0s;
  	background-image: none !important;
    background-color: #263238 !important;
  	border-color: #FFFFFF !important;
}.kpbb:hover, #gbqfb:hover{
  	border-color: #ffffff !important;
}
.sbibod, .gbqfqw{
  	border-color: #263238 !important;
  	border-right-width: 0;
	-webkit-transition: all 0.218s,visibility 0s;
	transition: all 0.218s,visibility 0s;
}.sbibod:hover, .gbqfqw:hover{
  	border-color: #ffffff !important;
}
#hdtb.notl div{
    background-color: #FFFFFF !important;
}
#cnt{
    background-color: #FFFFFF !important;
}
a:link{
  	color:#0091EA;
}
.s, .st, #rhs .g-blk.rhsvw, .kno-ecr-pt {
  	color:#263238 !important;
}
a.fl:link, .fl a, .flt, a.flt, .gl a:link, a.mblink, .mblink b {
	color: #263238 !important;
}
.mblink:visited, a:visited {
	color: #00BFA5;
}
#nav{
  	color: #ffffff !important;
}
#appbar{
  	background-color: #FFFFFF;
}
#hdtbSum{
  	border: none !important;
  	box-shadow: 0 2px 3px rgba(0,0,0,0.4);
}
._Rm, ._IId, ._Ck  cite {
	color: #263238 !important;
}
._vOc {
	color: #263238 !important;
}
.xpdopen {
	background-color: #FFFFFF;
}
.sbibod{
	border-right-width: 1px !important;
}
.jhp input[type="submit"], .sbdd_a input, .gbqfba {
  background-image: none !important;
  -webkit-border-radius: 2px;
  background-color: #009688;
  border: 1px solid #dcdcdc;
  border-radius: 2px;
  color: #FFF;
  -webkit-transition: all 0.218s,visibility 0s;
  transition: all 0.218s,visibility 0s;
}
.jhp input[type="submit"]:hover{
  background-color: #004D40;
  color: #FFF;
}
#fbar {
  background: #FFFFFF !important;
  border-top: none !important
}
}
