css:
@-moz-document domain("app.simplenote.com") {

/* Simplenote Minimalismo
   https://userstyles.org/styles/126009/simplenote-minimalismo
   by minimalismo
   v 1.0
*/


/* Hide notes list sidebar */

.sidebar {
  opacity:0 !important;
}

.sidebar:hover {
  opacity:1 !important;
  -webkit-transition: all .25s ease-in-out;
  -moz-transition: all .25s ease-in-out;
  -o-transition: all .25s ease-in-out;
  -ms-transition: all .25s ease-in-out;
  transition: all .25s ease-in-out;
  z-index:999 !important;
}

/* Hide toolbar */

.toolbar {
  opacity:0 !important;
}

.toolbar:hover {
  opacity:1 !important;
  -webkit-transition: all .25s ease-in-out;
  -moz-transition: all .25s ease-in-out;
  -o-transition: all .25s ease-in-out;
  -ms-transition: all .25s ease-in-out;
  transition: all .25s ease-in-out;
  z-index:999 !important;
}

/* Fade tagbar */

.tagbar {
  opacity:0.5 !important;
}

.tagbar:hover {
  opacity:1 !important;
  -webkit-transition: all .25s ease-in-out;
  -moz-transition: all .25s ease-in-out;
  -o-transition: all .25s ease-in-out;
  -ms-transition: all .25s ease-in-out;
  transition: all .25s ease-in-out;
  z-index:999 !important;
}

/* Hide footer */

.wrapper {
    bottom: 0px !important;
}

/* Remove footer text*/

/* (C) 2015 Automattic */
.footer li:first-of-type {
    display:none;
}

/* Everything */
.footer {
    display:none;
}

/* Remove grey borders */

.app, .toolbar, .popover .menu h4, .sidebar, .sideview, .tagbar {
    border: 0;
}

/* Remove shadow on notes list text*/

.notes li {
    text-shadow: none;
}

/* Remove shadow on search box */

.searchfield {
    -webkit-box-shadow: none;
    box-shadow: none;
}

/* Remove border from toolbar buttons */

.tbutton:hover {
    box-shadow: none !important;
}

/* Fix sidebar position to allow proper search box hover */

.sidebar {
    top:46px;
}

.sideview {
    top:0;
}

.notes {
    top:29px;
}

/* Center text area */

.note {
    right: 361px;
}

#txtarea {
    min-width: 650px;
    padding-right: 60px;
}

/* Change background color */

.app {
    background: 0;
}

.wrapper {
    background-color: #ffffff;
}

/* Change font color */

#txtarea {
    color: /*[[txtareacolor]]*/ !important;
}

/* Minimal scrollbars */

/*[[minimalscrollbar]]*/

/* Hide scrollbar in text area */

/*[[textscrollbar]]*/


}
