css:
@-moz-document domain("tvtropes.org") {

/*Dark Background*/
.main-full-width, .main-left.inner, .inner, .main-left, #header-new, .acaptionright, .section-link-row, .trope-body, .tab-content, .related.btn
{background: #181818!important;}

/*Dark Background and White Font*/
body, h1, h2, h3, h4, .folderlabel, span, .form-control, .folderlabelopen, .floatboxright, .legend, .gsc-control-cse, .gs-snippet, .gsc-option-menu-invisible, .textcolor, .dropdown-toggle
{background-color: #181818!important; color: #FFFFFF!important;}
  
/*Don't Display*/
.sidebar-right, .secondary-nav, .footer-top, .footer-bottom, .share-box, .user-img, .forumsigline, .gs-visibleUrl-long, .curr-subpage
{display: none;}

/*Link*/
a:link {color:#BA98CE!important;}
a:visited {color:#826A90!important;}
}
