css:
@-moz-document url-prefix("http://seasonvar.ru/serial") {
noindex,
iframe[style="position: fixed; display: block; top: 0px; left: 0px; width: 100%; height: 100%;"],
.td-for-left-block,
#___plusone_1td,
#player_msoc,
.sidebar.lside,
div [align="center"] {
    display: none !important;
    position: absolute;
    left: -9999px;
}

.pgs-player-inside,
.pgs-player-inside object {
    height: 754px;
}
.middle.left {
    border-left-width: 20px !important;
    padding-left: 20px !important;
}
#pjsfrrshtmlPlayer {
    display: block !important;
}
}

@-moz-document domain("seasonvar.ru") {
html body {
    padding-top: 1px !important;
    background-color: #080808;
}
noindex,
.lside-player div div {
    display: none !important;
    position: absolute;
    left: -9999px;
}
html body div iframe {
    display: none !important;
}
.wrapper {
    margin: 0px auto 0!important;
}
}
