@-moz-document domain("web.telegram.org") {

HTML{-webkit-filter:hue-rotate(/*[[hue]]*/deg);}

p, div{
color:#ace;
}
.im_message_author, .im_message_fwd_author {
    color: #aef;
    font-weight: 700;
}
a:focus, a:hover {
    color: #aae;
    text-decoration: underline;
}
div.im_dialog_peer > span{
	color:#aef;
}
/*list left*/


.im_dialogs_scrollable_wrap .active a.im_dialog:hover, .im_dialogs_scrollable_wrap .active a.im_dialog_selected {
    background-color: darkcyan;
}

.nav>li>a:focus, .nav>li>a:hover {
    text-decoration: none;
    background-color: cyan;
}

.im_dialogs_scrollable_wrap a.im_dialog:hover, .im_dialogs_scrollable_wrap a.im_dialog_selected {
    border-radius: 0;
    background: darkcyan;
}

/*TOP header*/

.tg_head_split {
    max-width: 100%;
    margin: 0 auto;
    height: 48px;
    background: darkcyan;
}

.dropdown.open .tg_head_btn, .tg_head_btn:hover {
    color: #fff;
    background: darkcyan;
}

