css:
@-moz-document url-prefix("https://www.netflix.com/watch/") {
.PlayerControls--button-control-row { bottom: 1px!important; margin-bottom: 2px; }

.PlayerControls--bottom-controls { transition: all 600ms ease-in-out; }
div.dimmed .PlayerControls--bottom-controls, div.inactive .PlayerControls--bottom-controls { transform: translateY(8.6em); display: block!important; visibility: visible!important; }
div.inactive .PlayerControls--bottom-controls { opacity: 0.5!important; }

/* subtitles */
.player-timedtext-text-container { bottom: 11%!important; }
.player-timedtext-text-container span { 
    font-family: Arial,Helvetica,sans-serif,sans!important;
    text-shadow: -1px 1px 12px #000!important;
}
}
