css:
@-moz-document url-prefix("https://www.facebook.com/"), domain("www.facebook.com") {
/* hide right column */
#rightCol{display:none;}

/* fix content column */
#contentCol{margin-left:200px!important;background-color:rgba(0, 0, 0, 0)!important;}
#contentArea{
    margin-left:0px!important;
    right:0!important;
    width:850px!important;}

/* blue bar */
._2s1y{
    background-image: none !important;
    background-color: #4a4a4a !important;
    border-bottom: 1px solid #282828 !important;}

/* background */
body{background-color:#7f7f7f!important;}
#content_container{background-color:#7f7f7f;}

/* left hand column */
#leftCol{position:fixed;}
#leftCol>div>div{
       margin-left:-14px;
      padding-left: 16px !important;
        margin-top: 13px;
       padding-top: 15px !important;
     margin-bottom: 13px;
    padding-bottom: 15px !important;
  background-color: #b4b4b4 !important;
}

/* page margin */
._1qkq{margin-left:12px!important;}
}
