/* Made from the original 'Searx.me dark' work by stbinan
   at https://userstyles.org/styles/115066/searx-me-dark

   Feel free to create new themes from this one with your
   own background pictures, but if so please keep credits
   and rewards to the original author: stbinan

   If the theme doesn't apply just click the Preferences
   link then choose 'Oscar' then 'Pointhi' in the Theme
   selection menu                                         */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("searx.me"), domain("searx.de"), domain("searx.info"), domain("searx.laquadrature.net"), domain("searx.oe5tpo.com"), domain("seeks.hsbp.org"), domain("searx.brihx.fr"), domain("search.kujiu.org"), domain("searx.new-admin.net"), domain("search.homecomputing.fr"), domain("searx.potato.hu"), domain("s3arch.eu"), domain("search.jpope.org"), domain("searx.fossencdi.org"), domain("blackholeroutercondition.com"), domain("ready.pm"), domain("searx.schrodinger.io"), domain("search.blackit.de"), domain("heraut.eu"), domain("searx.aquilenet.fr"), domain("search.azkware.net"), domain("searx.netzspielplatz.de"), domain("searx.32bitflo.at"), domain("searx.nulltime.net"), domain("posativ.org"), domain("quackquackgo.nl"), domain("suche.elaon.de"), domain("search.koehn.com"), domain("search.notoriousdev.com"), domain("searx.4ray.co"), domain("iguana.cubeclan.ch"){
  body {
    color: #aaa;
    background-size: cover !important;
    background-color: #FFF;
    /* One of the very few things I've changed from the original stbinan theme is the following line */
    background: url("/*[[change-background]]*/") fixed center !important;
}
  .searx-navbar {
    background-color: #101010;
    border-color: rgba(0,0,0,0);
}
  .searx-navbar .navbar-nav > .active > a, .navbar-default .navbar-nav > .active > a:hover, .navbar-default .navbar-nav > .active > a:focus {
    color: #555;
    background-color: #050505;
}
  .form-control {
    color: #bbb;
    background-color: #242424;
    background-image: none;
    border: 1px solid #666;
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.075) inset;
}
  .btn-default {
    color: #bbb;
    background-color: #242424;
    border-color: #666;
}
  .highlight {
    background: none;
    font-weight: 900;
}
  .panel {
    background-color: #101010;
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.05);
}
  .panel-default {
    border-color: #666;
}
  .panel-default > .panel-heading {
    color: #bbb;
    background-color: #050505;
    border-color: #666;
}
  .form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
    background-color: #242424;
}
  .label-default {
    background-color: rgba(0,0,0,0);
}
  .label {
    color: #bbb
  }
  .table-striped > tbody > tr:nth-child(2n+1) > td, .table-striped > tbody > tr:nth-child(2n+1) > th {
    background-color: #101010;
}
  .table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    border-top: 0px solid #DDD;
}
  
  a {
    color: #bbb;
}  
  
/* The lines bellow have been modified by me (FenyX), so if something's wrong here it's my fault ;) */
  
  .text-hide img {
    height: 0 !important;
    width: 0 !important;
    /* these numbers match the new image's dimensions */
    padding-left: 700px !important;
    padding-top: 155px !important;
    background: url("http://i.imgur.com/SZngYUN.png") no-repeat !important;
}
  
  .well {
    background-color:#242424 !important;
    border:1px solid #666 !important;
  }
  
}
