@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("liberateyourdemon.com") {
  html {
    background: #222 url(/*[[Image]]*/) repeat-x 50% 1px;
  }
  
  #content {
    background: #222;
  }
  
  #content .pageContent, .mainContainer, .footer .pageContent {
    background: #444;
  }
  
  .breadcrumb, .breadcrumb .crust a.crumb, #content .nodeInfo, .sidebar .secondaryContent, .sectionMain, .discussionListItem .posterAvatar, .discussionListItem .stats, .discussionListItem .main, .discussionListItem .lastPost, .messageList .message, .messageText, .textCtrl, .quickReply, .redactor_toolbar {
    background: #656565 !important;
    color: #bfbfbf;
  }
  
  .redactor_box .placeholder span {
    color: #444;
  }
  
  .nodeTitle a, .nodeTitle a:visited {
    color: #eec;
  }
  
  .nodeTitle a:hover {
    color: #E66800;
  }
  
  .textCtrl {
    box-shadow: 2px 2px 5px #aaa;
    margin-right: 4px;
  }
  
  .breadcrumb .crust .arrow span {
    filter: invert(0.61) !important;
  }
  
  .avatar img, .avatar .img, .avatarCropper, .message .likesSummary, .sidebar .visitorPanel .avatar img, .avatar .img, .avatarCropper {
    background-color: #999;
    border: 1px solid #aaa;
    border-radius: 50px;
  }
  
  .discussionListItem .posterAvatar .miniMe img {
    border-radius: 15px;
  }
}
