css:
@-moz-document domain("www.baidu.com"), domain("www1.baidu.com") {
.container_l, .op-img-star-divide-low > a {
    width: auto !important;
}

/*[[column]]*/

.s_tab, .nums, .search_tool_conter, #rs, .to_tieba, .to_zhidao_bottom {
    margin: 0 auto!important;
}

#page, #s_tab {
    padding-left: 0px !important;
    text-align: center!important;
}

body[link='#0000cc'] .s_form {
    width:760px!important;
    margin:0 auto !important;
    transform:translatex(-40px);
}

#content_right, .c-recommend, body > div[class="result-op xpath-log"], #content_left > * {
    display: none !important;
}

}

@-moz-document domain("www.baidu.com"), domain("www1.baidu.com") {
/*For Super_preloaderPlus script*/
#sp-fw-container {
    transform:translatex(-200px) translatey(13px);
}
}

@-moz-document domain("www.baidu.com"), domain("www1.baidu.com") {
/*去除无用链接*/
#rs, .to_tieba, .to_zhidao_bottom {
    display:none;
}
}
