css:
@-moz-document url-prefix("https://discordapp.com/channels/") {
body {
    background: url('/*[[background]]*/') !important;
    background-size: cover !important;
}

div[class$=-wrapper],
div[class$=-wrap],
div[class$=-channels],
.friends,
.content,
.account,
.links,
.account .btn,
.channel-text.selected,
::-webkit-scrollbar-track-piece,
header {
    background: rgba(0,0,0,0.1) !important;
}

.channel-members {
    background: rgba(0,0,0,0.7) !important;
}

::-webkit-scrollbar-thumb,
::-webkit-scrollbar-track-piece {
    border-color: rgba(60,60,60,1) !important;
}

.guild-header ul {
    top: 0px !important;
    background: rgba(0,0,0,0.9) !important;
}

.guild-header.guild-header-open ul {
    top: 56px !important;
}
}
