css:
/* =========================== server settings =========================== */
@-moz-document domain("/*[[ip]]*/") {
/* ======================= end server settings =========================== */
/*         # v 1.0 Initial Release for Transmission Web Interface          */
/* ========================== Global Settings ============================ */

html, body{
background: url("http://res.cloudinary.com/mrquinn/image/upload/v1460652230/styles/OldWood_Grey.jpg") #000000 fixed repeat !important; color:#cccccc!important;
}
.odd {  /* light row */
    background-color: #6E6969 !important;
}
.even { /* dark row */
    background-color: #444141 !important;
}
/* === Transmission 2.92 (14714) Web interface === */

    div[id="toolbar"],
    div[id="statusbar"],
    div[class="torrent_footer"] {
    background: url("http://res.cloudinary.com/mrquinn/image/upload/v1460652230/styles/header_bg.gif") !important;
    }
    div[id="statusbar"] {
        color: #fff !important;
    }
    ul[id="torrent_context_menu"],
    ul[id="menu-item"],
    li[class="ui-menu-item"] {
        background-color: #000 !important;
    }
    div[class="inspector-page ui-helper-hidden"],
    li[class="torrent selected"],
    li[class="torrent even selected"]{
    background: url("http://res.cloudinary.com/mrquinn/image/upload/v1460652799/styles/trans_back.png") !important;
    }
    li[class="torrent"],
    li[class="inspector_tracker_entry odd"] {
        background-color: #6E6969 !important;
    }
    ul[class="tier_list"] {
        background-color: #ccc;
        color: #fff;
    }
    li[class="inspector_tracker_entry odd"] {
        color: #fff;
    }
    li[class="inspector_tracker_entry even"] {
        color: #fff;
    }
    div[class="tracker_activity"],
    table[class="tracker_stats"],
    div[class="torrent_progress_details"],
    div[class="torrent_peer_details"],
    div[class="inspector_torrent_file_list_entry_progress"] {
        color: #fff !important;
    }
    div[class="tracker_host"],
    div[class="torrent_name"],
    div[class="inspector_torrent_file_list_entry_name"],
    div[id="torrent_inspector_name"]{
        color: #FFE081 !important;
    }
    div[class="inspector_group_label"] {
        color: #fff;
    }
div[class="inspector-page"],
div[id="inspector_trackers_list"],
div[id="inspector-page-trackers"],
div[id="inspector-page-peers"],
div[id="inspector-page-files"] {
    background: url("http://res.cloudinary.com/mrquinn/image/upload/v1460652799/styles/trans_back.png") !important;
        color: #fff !important;
        padding: 5px;
    }
    /* === end Transmission 2.92 (14714) torrent upload page === */
}
