css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("iwakuroleplay.com") {   

  	/* Colours names in /me messages in the chatbox in the colour of the message. */

    #siropuChat .siropuChatMe .username:first-child {

        color: inherit;

    }

    

    /* Benefactor. Default: hsl(3, 87.7%, 41.6%) */

     .username .style40 {

        color: /*[[benefactor]]*/ !important;

    }


  
    .userBanner.ribbonBenefactor {

        background-color: /*[[benefactor]]*/ !important;

        border-color: /*[[benefactor]]*/ !important;

    }



    /* Server. Default: hsl(56, 38.3%, 58%) */

     .username .style5 {

        color: /*[[server]]*/ !important;

    }


  
    .userBanner.ribbonServer {

        background-color: /*[[server]]*/ !important;

        border-color: /*[[server]]*/ !important;

    }



  	/* Lurker. Default: hsl(0, 0%, 50.2%) */

     .username .style32 {

        color: /*[[lurker]]*/ !important;

    }



  	/* Inactive Users. Default: hsl(0, 0%, 32.2%) */

     .username .style33 {

        color: /*[[inactive]]*/ !important;

    }



  	/* Regular Users. Default: hsl(120, 35%, 40%) */

     .username .style2 {

        color: /*[[reguser]]*/ !important;

    }

    

    /* Administrators. Default: hsl(350, 70%, 40%) */

    .username .style3 {

        color: /*[[admin]]*/ !important;

    }



    .userBanner.ribbonAdmin {

        background-color: /*[[admin]]*/ !important;

        border-color: /*[[admin]]*/ !important;

    }

    

    /* Staff Ribbon / Security Department. Default: hsl(220, 50%, 50%) */

    .username .style4 {

       color: /*[[security]]*/ !important;

    }

    

    .userBanner.ribbonSecurity {

        background-color: /*[[security]]*/ !important;

        border-color: /*[[security]]*/ !important;

    }

    

    /* Community Department. Default: hsl(270, 40%, 50%) */

    .username .style31 {

       color: /*[[community]]*/ !important;

    }

  
    
    .userBanner.ribbonCommunity {

        background-color: /*[[community]]*/ !important;

        border-color: /*[[community]]*/ !important;

    }

    

    /* Maintenance Department. Default: hsl(170, 40%, 50%) */

    .username .style39 {

       color: /*[[maintenance]]*/ !important;

    }


  
    .userBanner.ribbonMaintenance {

        background-color: /*[[maintenance]]*/ !important;

        border-color: /*[[maintenance]]*/ !important;

    }

    
  
    /* Donating Members. Default: hsl(155, 55%, 50%) */

    .username .style14 {

        color: /*[[donor]]*/ !important;

    }

    

    .userBanner.ribbonDonor {

        background-color: /*[[donor]]*/ !important;

        border-color: /*[[donor]]*/ !important;

    }



    /* Intern Moderators. Default: hsl(200,65%,50%) */

    .username .style16 {

        color: /*[[intern]]*/ !important;;

    }



    .userBanner.ribbonVolunteer {

        background-color: /*[[intern]]*/ !important;

        border-color: /*[[intern]]*/ !important;

    }

    

    /* New Users. Default: hsl(100, 50%, 60%) */    

    .username .style30 {

        color: /*[[new]]*/ !important;

    }

}
