@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(speedof.me) {

/* Hides Header*/
h1[class="header"] {display: none !important}

/* Hides "Adblocker detected!" Side Panels */
div[id*="adpane"], div[class*="adpane"] {display: none !important}

/* Hides Ad */
div[class*="ad"] {display: none !important}

/* Makes Main Pane 100^ Width */
div[class="pane"], div[class="mainpane"], div[id="chartContainer"], div[class="main"] { width: 100% !important; }

/* Hides Footer */
div[class="footer"] {display: none !important}
	
}
