css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eldarya.com"), domain("eldarya.fr") {

/*Page du familier : image*/  
  
  #pet-portrait {
    background : url("http://nsm08.casimages.com/img/2016/05/26//16052611311510339314259478.png") center no-repeat !important ; /* !changer le lien du fond - change the background link! */
    background-size : auto 100% !important ;
    height : 450px !important ;
  }
  
  #pet-portrait > img[src*="/static/img/pet/icon/"] {
    background: url("http://nsm08.casimages.com/img/2016/05/26//16052611313010339314259480.png") no-repeat !important ; /* !changer le lien du familier - change the pet link! */
    padding-left : 300px !important ; /* !mettre la largeur de votre image de familier - write the width of your pet picture! */
    padding-top : 287px !important ; /* !mettre la hauteur de votre image de familier - write the height of your pet picture! */
    margin-left : calc((450px - 300px) / 2) !important ; /* !changer le 300 pour la largeur de votre image de familier - change the 300 with the width of your pet picture ! */
    margin-top : 125px !important ; /* !changer la position verticale de l'image du familier - change the pet position vertically ! */
    width : 0 !important ;
    height : 0 !important ;
    
}
  
/*Page du familier : infos */ 
  
  /*Infos*/

  #pet-info-name:before {
    content : "Pikachu" !important ; /* !changer le nom du familier - change the pet name! */
    font-size : 20px !important ;
  }

  #pet-info-name {
    font-size : 0 !important ;
  }  

  #pet-info-evolve > .on {font-size : 0 !important ;}

  .pet-info-evolve-icon {font-size : 13px !important ;}

  .pet-info-evolve-icon:after {
    content : " L'évolution de Pikachu nécessite une pierre de foudre" ; /* !changer le message d'évolution du familier - change the message about the evolution! */
  }

  #pet-info-racegender:before {
    content : "Pokemon électrique" !important ; /* !changer le type du familier - change the type of the pet! */
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 

  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  

  img[src*="/static/img/f.png"] { /*changer les familiers femelles en mâles si nécessaire - change a female pet into male pet*/
    background : url("http://www.eldarya.fr/static/img/m.png") !important ;
    padding-left : 14px !important ;
    padding-top : 15px !important ;
    width : 0 !important ;
    height : 0 !important ; 
    margin-bottom : 5px !important ;
  }


 /*Nourriture - food*/

  #pet-info-food > img {
    background : url("http://nsm08.casimages.com/img/2016/05/26//16052611311210339314259477.png") !important ; /* !changer le lien de la nourriture - change the food link! */
    background-size : 70px 70px !important ;
    padding-left : 70px !important ;
    padding-top : 70px !important ;
    width : 0 !important ;
    height : 0 !important ;
} 

  #pet-info-food > div {font-size : 0 !important ;}

  #pet-info-food > div > span {font-size : 20px !important ;}

  #pet-info-food > div:before {
    content : "Baies et pokeblocs " !important ; /* !changer le nom de la nourriture - change the food name! */
    font-size : 20px !important ;
    text-align : center !important ;
  }

  #pet-info-food > span {font-size : 16px !important ;}

  
/* Médaillon du familier (en haut dans la bannière) - round with the pet (top of the page) */
  
  #pet-headerPortrait #pet-headerPortrait-base {
    background : url("http://nsm08.casimages.com/img/2016/05/26//16052611313010339314259480.png") no-repeat, url("http://nsm08.casimages.com/img/2016/05/26//16052611311510339314259478.png") no-repeat !important ; /* !changer le lien du familier, puis le lien du fond - change the pet link, then the background link! */
    background-size : auto 115%, auto 100% !important ; /* !changer la largeur hauteur du familier, changer la hauteur largeur du fond - change the pet width height, change the background width, height! */
    background-position : 0 0, 0 0 !important ; /* !changer le placement du familier, changer le placement du fond - change the pet position, change the background position! */
  }

}

@-moz-document url("http://www.eldarya.com/player/profile//*[[S9Clay]]*/"), url("http://www.eldarya.fr/player/profile//*[[S9Clay]]*/") {  
  
/*Familier sur le profil de gardienne - Pet on the gamer's profile*/
  
  #player-display-pet {
    background : url("http://nsm08.casimages.com/img/2016/05/26//16052611313010339314259480.png") center no-repeat !important ; /* !changer le lien du familier - change the pet link! */
    background-size : auto 100% !important ;
    height :  191px !important ; /* !changer la hauteur familier - change the pet height! */
    width :  200px !important ; /* !changer la largeur du familier - change the pet width! */
  }

	#player-display-pet > img {
    display : none !important ;
  }

/*[[CSSboss]]*/
  
}
