#count-online-toolbar { display: none !important; }
#area-toolbar-view { line-height: 40px; box-shadow: 0 1px 2px rgba(0, 0, 0, 0.05); padding: 0 16px 0 6px; }
#block-info-view { width: 900px }
.main-bottom .channel-rank .fame-hall .module-main .module-con li p .follow { color: #FFA700; }
.header.fixed .nav { position: fixed; top: 46px !important; left: 0; box-shadow: 0 5px 5px rgba(107, 107, 107, 0.52); background: rgba(255, 255, 255, 0.89); width: 100%; padding-bottom: 0; }
.header .header-guide { height: 46px; display: inline-block; text-align: right; margin-right: 870px; }
