.main-bottom .channel-operate .area-change-btn { cursor: pointer; margin-top: 3px; margin-left: 650px; }
.area .area-title { border: 2px solid #FFA700; height: 24px; line-height: 22px; width: 74px; text-align: center; border-radius: 3px; margin-top: 8px; }
