css:
@-moz-document url("http://socialani.me/") {
.copyright {
background-image: url("/*[[Fondo]]*/");
background-size: cover;
background-position: right bottom;
z-index: 9990!important;
content: "" !important;
}
.copyright a{
background: transparent url("http://i.imgur.com/Abbn2LK.png") no-repeat;
background-position: 50% 10%;
background-size: 80%;
content: "" !important;
color: black !important;
font-size: 32px !important;
padding-top: 15%;
z-index: 9990!important;
top: 0% !important;
}
.copyright a:after{
background: transparent url("/*[[Chica]]*/") no-repeat;
background-size: 50%;
content: "";
color: black !important;
font-size: 32px !important;
z-index: 9990!important;
}

.copyright{
background-color: black;
background-attachment: fixed;
background-repeat: no-repeat;
height: 100% !important;
left: 0 !important;
position: fixed !important;
top: 0 !important;
width: 100% !Important;
z-index: 101;
color: transparent;
content: "";
pointer-events: none;
opacity: 0;
-moz-animation:mymove 5s; /* Firefox */
-webkit-animation:mymove 5s; /* Safari and Chrome */
z-index: 9990!important;
}
.copyright a {
position: fixed;
content: ""!important; 
color: transparent!important; 
height: 100%;
width: 45%;
left: 0 !important;
pointer-events: none;
opacity: 0;
z-index: 110 !Important;
-moz-animation:mymove 5s; /* Firefox */
-webkit-animation:mymove 5s; /* Safari and Chrome */
z-index: 9990!important;
}
.copyright a:after
{
position: fixed;
height: 100%;
width: 100%;
right: 0 !important;
top: 0 !important;
z-index: 110;
pointer-events: none;
opacity: 0;
background-position: 100% 0;
padding-top: 20%;
-moz-animation:mymove2 5s; /* Firefox */
-webkit-animation:mymove2 5s; /* Safari and Chrome */
    z-index: 9990!important;
}

@-moz-keyframes mymove /* Firefox */
{
0% {opacity: 1}
80% {opacity: 1}
95% {opacity: 0}
99% {opacity: 0}
}


@-moz-keyframes mymove2 /* Firefox */
{
0% {opacity: 1; background-position: -100% 0;}
40% {opacity: 1; background-position: 100% 0;}
80% {opacity: 1}
95% {opacity: 1}
99% {opacity: 0}
}

@-webkit-keyframes mymove /* Safari and Chrome */
{
0% {opacity: 1}
80% {opacity: 1}
95% {opacity: 0}
99% {opacity: 0}
}

@-webkit-keyframes mymove2 /* Safari and Chrome */
{
0% {opacity: 1; background-position: 0% 0;}
40% {opacity: 1; background-position: 100% 0;}
80% {opacity: 1}
95% {opacity: 1}
99% {opacity: 0}
}


/*----------------*/

.cookies{
    z-index: 9980!important;
}
nav.navbar.navbar-default.navbar-fixed-top{
z-index: 9980!important;
}
}
