css:
@-moz-document domain(carrot.com) {





/*[[width]]*/

/*[[spin]]*/

/* paint the text white baby */
body,
html,
.layout-user-detail .feed-item__subreddit,
.user-info__link,
.user-info__value,
.new-headline.new-headline--h1,
.room-status__flair-text,
.account-box__menu-item,
.chat-msg.chat-msg--room-join,
.replybox__input,
.feed-item__subreddit,
.cells-heading,
.cell__accessory__badge,
.cell.cell--chat .cell__title--avatar,
.chat-msg__moving-part,
.chat-msg__text,
.chat-sidepanel__room-slug {
    color: #fff !important;
    text-shadow: none;
}

/* spice up the colors! This is the main background color: #2E3136 */
.chat-thread,
.room-list-tab__paginiator-buttons,
.chat-sidepanel__action-button,
.chat-sidepanel__action-button, .dropbtn,
.replybox__input-wrapper,
.layout-page.layout-page--chat,
.app-handler,
.search__categories span,
.chat-sidepanel__action-button,
.dropbtn,
.layout-user-detail,
.cell--member:hover,
.chat-thread__thread {
    background-color: #2E3136 !important;
    border: none !important;
}


/* spice up some more colors! This is the secondary background color: #36393E */
.aside,
.cell.cell--chat .cell__content:hover,
.account-box__speaker,
.account-box__menu-item,
.search__top-area,
.feed-item__online-member,
.feed-item,
.top-area,
.layout-side,
.replybox__input,
.cell.cell--chat.cell--active .cell__content,
.user-info__rooms {
    background-color: #36393E !important;
    border: none !important;
    box-shadow: none !important;
}


/* kill the nav shadow. kill it dead. (actually just make a new one) */
.top-area {
    box-shadow: 0px 0px 1px #000 !important;
}

/* gimme some space baby! Let the text box breath */
.chat-thread__thread {
    margin-bottom: 4vh !important;
}


/* make the scrollbars pretty. so smooth! */
/* Scrollbar Chrome [credit to DarkTube v3 by Nass O] */
*::-webkit-scrollbar {
    width: 10px ! important;
    height: 10px ! important;
    background: #1e1e1e ! important;
    border: 1px solid #252525 ! important;
}

*::-webkit-scrollbar-button {
display: none ! important;
}

*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
border: 0 ! important;
box-shadow: none ! important;
}

*::-webkit-scrollbar-thumb {
    min-height: 28px ! important;
    background: #1E2124 ! important;
}

*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
background: #2E3136 ! important;
}
    

    

    
    
}
