css:
@-moz-document domain("airliners.net") {
  /* Stylish style sheet to restore some of */
  /* the old look and feel of airliners.net */

  /* define some common colours to copy/past/search-replace*/
  /* rear  background-colour rgb(70, 106, 152) */
  /* fore  background-colour rgb(27, 55, 76) */
  /* alternate  background   rgb(61, 94, 127) */

  /* GENERAL */
  
  /* remove white space between all menu items */
  .submenu li {
    line-height: 20px !important;
  }
  
  /* highlight links in royal blue */
  a:hover,
  ul li a:hover {
    background-color: royalblue !important;
  }
  
  /* highlight usernames in yellow */
  .username {
    color: yellow !important;
  }

  /* colour and size navigation buttons */
  .pagination-trigger,
  .btn {
    color: black !important;
  }
  .pagination li a{
    background-color: lightgray !important;
    color: black !important;
  }
  .pagination-sm {
    margin-bottom: 0px !important;
  }

  /* colour of triangles on "dialog" boxes */
  .ul-form-triangle {
    border-bottom-color: rgb(27, 55, 76) !important;
  }
  
  /* white spaces */
  .space10 {
    height: 2px !important;
  }
/*[[StretchSpan]]*/
/*[[UserColourText]]*/
  #airliners-body-container {
    margin-left: 4px;
    margin-right: 4px !important;
  }
  
  /* change colour of drop-down menus */
  .dropdown-menu {
    background-color: rgb(27, 55, 76) !important;
  }

  /* change colour of links and muted text */
  a,
  ul li a {
    color: lightgrey !important;
  }
  .text-muted {
    color: lightgrey;
  }

  /* change colour of form labels */
  .field-label {
    color: lightgrey;
  }

/*[[QueenOfSkies]]*/
  /* change body colour */
  body {
    background-color: rgb(70, 106, 152) !important;
  }

/* prevent search and login buttons changing on resize */
  #airliners-header > div.not-centered-navigation-wrapper.not-centered-navigation-wrapper-one > nav > ul > li.nav-link.nav-link-small.mobile-menu-nav-link,
  #airliners-header > div.not-centered-navigation-wrapper.not-centered-navigation-wrapper-one.not-cnw-circles > nav > ul > li.nav-link.nav-link-small.mobile-menu-nav-link,
  #airliners-header > div.not-centered-navigation-wrapper.not-centered-navigation-wrapper-two > nav > ul > li.nav-link.nav-link-search-form.simple-search-form.hide,
  #airliners-header > div.not-centered-navigation-wrapper.not-centered-navigation-wrapper-two > nav > ul > li.nav-link.nav-link-small.nav-link-search-small.simple-search-form-trigger {
    display: none !important;
  }
  #airliners-header > div.not-centered-navigation-wrapper.not-centered-navigation-wrapper-one > nav > ul > li.nav-link.nav-link-sign-in-large,
  #airliners-header > div.not-centered-navigation-wrapper.not-centered-navigation-wrapper-one.not-cnw-circles > nav > ul > li.nav-link.nav-link-notifications.nav-link-user-menu.more,
  #airliners-header > div.not-centered-navigation-wrapper.not-centered-navigation-wrapper-one.not-cnw-circles > nav > ul > li.nav-link.nav-link-profile.nav-link-user-menu.more,
  #airliners-header > div.not-centered-navigation-wrapper.not-centered-navigation-wrapper-two > nav > ul > li.nav-link.nav-link-search-large.simple-search-form-trigger {  
    display: inline-block !important;
  }
  
  /* colour and remove whitespace in activity/user drop-down menus */
  #airliners-header > div.not-centered-navigation-wrapper.not-centered-navigation-wrapper-one.not-cnw-circles > nav > ul > li.nav-link.nav-link-profile.nav-link-user-menu.more > ul,  
  #airliners-header > div.not-centered-navigation-wrapper.not-centered-navigation-wrapper-one.not-cnw-circles > nav > ul > li.nav-link.nav-link-notifications.nav-link-user-menu.more > ul {
    margin-top: 0 !important;
  }
  #top > div > nav > div.user-login.hidden-xs > ul > li.dropdown.dropdown-avatar.dropdown-notification.open > ul > li:nth-child(1),
  #notification_list > div.navbar-footer,
  #airliners-header > div.not-centered-navigation-wrapper.not-centered-navigation-wrapper-one.not-cnw-circles > nav > ul > li.nav-link.nav-link-profile.nav-link-user-menu.more > ul > li a,  
  #airliners-header > div.not-centered-navigation-wrapper.not-centered-navigation-wrapper-one.not-cnw-circles > nav > ul > li.nav-link.nav-link-notifications.nav-link-user-menu.more > ul > li > span > div > a {
    background-color: rgb(70, 106, 152);
  }
  #notification_list > div.navbar-footer > div.navbar-footer-content {
    padding: 4px !important;
  }
  .unm-notification-item {
    padding: 0px !important;
  }
  .unm-notification-item,
  .submenu-spacer {
    background-color: rgb(70, 106, 152) !important;
  }
  .notification-block {
    padding-bottom: 0 !important;
    padding-top: 0 !important;
    font-size: 13px;
    line-height: normal;
  }
  .notification-block > img {
    margin-top: 4px;
  }
  
  /* colour the various header menu items white */
  .nav-link-sign-in-large, 
  .fa-user, .fa-search, .fa-home {
    color: white !important;
  }

  /* move and colour the header drop-down menus */
  .centered-navigation-menu li .submenu {
    background-color: rgb(27, 55, 76) !important;
    border-color: rgb(232, 236, 243);
    border-radius: 1px;
    border-style: solid;
    border-width: 2px;
    margin-top: 10px !important;
  }
  header.centered-navigation ul.centered-navigation-menu li.nav-link.more > ul > li:first-child a,
  header.centered-navigation ul.centered-navigation-menu li.nav-link.more > ul > li:last-child a{
    padding-bottom: 0;
    padding-top: 0;
  }
  /* remove white space on navigation bar */
  .navbar-static-top {
    background-color: rgb(27, 55, 76);
    min-height: 40px;
    height: 40px;
    padding-top: 0px;
  }
  #top > div > nav > div.user-login.hidden-xs > ul > li,
  #top > div > nav > ul > li {
    height: 40px !important;
  }
  #top > div > nav > div.user-login.hidden-xs > ul > li > a,
  #top > div > nav > ul > li > a {
    height: 40px;
    padding-top: 8px;
    padding-bottom: 5px;
  }

  /* FOOTER */
  
  /* change footer background colour */
  .footer-top {
    background-color: rgb(70, 106, 152) !important;
  }

  /* remove all of the footer white space */
  .footer-top,
  .footer-bottom,
  .footer-copyright {
    margin-bottom: 0 ;
    padding-bottom: 5px !important;
    padding-top: 5px !important;
  }
  .footer-top,
  .footer-top-title {
    color: lightgrey !important;
    margin-bottom: 0 !important;
    padding-bottom: 0 !important;
    padding-top: 0 !important;
  }
  
  /* HOMEPAGE www.airliners.net */
  
  /* change colour of announcements */
  .home-block-content {
    color: white !important;
    background-color: rgb(70, 106, 152) !important;
    box-shadow: 2px 2px 1px 3px rgb(64, 64, 80) !important;
  }
  
  /* remove numbers from photo thumbnails */
  .htp-thumbnail-image-rank {
    display: none !important;
  }

/*[[HideAds]]*/
  .htp-thumbnail-details-content {
    background-color: rgb(70, 106, 152) !important;    
  }
  
  /* FORUMS GENERAL www.airliners.net/forum/ */

  /* change background colour of the home button */
  .breadcrumb li:first-child a {
    background-color: rgb(61, 94, 127);
  }

  /* remove whitespace */
  .panel-forum {
    margin-bottom: 4px ;
  }
  #page-body > div.row > div {
    padding-left: 8px ;
    padding-right: 8px ;    
  }
  #page-body > div.row > div > div > div.panel-body {
    line-height: normal;
    padding: 4px ;
    padding-left: 6px;
  }
  #page-body > div.row > div > div > div.panel-body > p {
    margin-bottom: 2px;
  }

  /* remove indentation of search box and page navigation buttons */  
  #page-body > div.row > div.col-md-4,
  #page-body > div.row > div.col-md-8 {
    padding-left: 0 ;
    padding-right: 0 ;
  }
  
  .panel-forum{
    background-color: rgb(27, 55, 76);
    color: white;
    border-bottom-style: none;
    border-left-style: none;
    border-right-style: none;
    border-top-style: none;
  }
  .panel-inner {
    border-color: black;
    border-style: solid;
    border-width: 2px;
  }
  .section-viewforum,
  .section-viewtopic{
    background-color: rgb(70, 106, 152);
    color: white;
    margin-bottom: 0;
  }
  .breadcrumb-line,
  .breadcrumb {
    background-color: rgb(61, 94, 127);
    color: white;
    margin-bottom: 0;
  }

  /* FORUM TOPICS www.airliners.net/forum/viewforum/ */

  /* no need for a "topic" header - we're not stupid */
  /* remove white space and and colour main panels */
  .panel-forum div.panel-heading {
      display: none;
  }
  
  /* change borders and colours of the topics list table */
  .table-primary {
    border-style: none;
  }
  .table-primary thead tr th {
    border-bottom-style: solid;
    border-color: black;
    border-top-style: solid;
    border-top-width: 4px;
    border-width: 2px;
    color: black !important;
  }

  /* change colours and remove white space in list */
  .desc-wrapper {
      color: darkgrey;
      padding-top: 2px !important;
      padding-bottom: 2px !important;
  }
  .last-wrapper {
      color: darkgrey;
  }

  /* change colour of topic titles */
  .topictitle {
    color: white !important;
    font-weight: bold;
  }
  
  /* reduce size of the icons */
  .row-icon-font {
    height: 30px;
    line-height: 30px;
  }
  .icon-wrapper {
    height: 32px !important;
    margin-top: 3px !important;
    width: 32px !important;
  }
  
  /* change colour and remove whitespace in rows */
  .footable-visible {
    background-color: rgb(27, 55, 76) !important;
    border-top-color: black !important;
    border-top-width: 1px !important;
    color: white !important;
    line-height: 16px !important;
    padding-bottom: 2px !important;
    padding-top: 0 !important;
  }

  /* change colour and style of the statistics column */
  .stats-col {
    /* messy but only way I can get rid of the stats column background colour
       as they have made it important (can't be overwridden)'*/   
    background-image: linear-gradient(to right, rgb(27, 55, 76) 99%, rgb(27, 55, 76) 100%) !important;
    padding-left: 0 !important;
    padding-right: 0 !important;
  }
  .stats-wrapper {
    background-color: rgb(27, 55, 76) !important;
    border-bottom-width: 1px !important;
    border-top-color: black !important;
    border-top-style: none !important;
    border-top-width: 0px !important;
    color: lightgray !important;
    line-height: 16px !important;
    padding-bottom: 0 !important;
    padding-left: 8px !important;
    padding-right: 8px !important;
    padding-top: 0 !important;
  }
  
  /* move topic page buttons up a bit */
  .pagination-forum {
    margin-top: -10px;
  }

  /* FORUM POSTS www.airliners.net/forum/viewtopic/ */

  /* change colour and borders of each post */
  .forum-answer {
    background-color: rgb(27, 55, 76);
    border-color: black;
    border-style: solid;
    border-width: 2px;
    margin-bottom: 4px;
  }
  .forum-answer .panel-body {
    margin-top: -18px;
    padding-bottom: 2px;
    padding-left: 5px;
    padding-right: 5px;
    padding-top: 0px;
  }
  .panel-footer {
      background-color: rgb(27, 55, 76) !important;
  }
  .postbody,
  .postbody .content {
    background-color: rgb(27, 55, 76);
    color: white;
  }
  .postbody .content {
    line-height: normal;
    margin-top: 8px;
  }

  /* reduce font size and change colour of signatures */
  .post-signature {
    color: lightgrey;
    font-size: 8pt !important;
    line-height: 10pt;
    margin-top: 4px;
  }
  #preview .text-center,
  .postbody .text-center {
    display: none !important;
    height: 0 !important;
  }
  
/* reduce size of the post header */
  .forum-answer .panel-heading {
    border-style: none !important;
    height: 50px !important;
    padding-bottom: 3px;
    padding-left: 8px;
    padding-right: 8px;
    padding-top: 3px;
  }

  /* remove the repeated thread title from each post */
  .forum-answer .panel-heading .clearfix .side-segment {
    display: none;
  }

  /* change colour of quotes, normal line spacing */
  blockquote {
/*[[QuoteColour]]*/
    color: lightgrey;
    margin-bottom: 0px !important;
    margin-top: 0 !important;
    padding-bottom: 0px !important;
    padding-top: 0px !important;
    line-height: normal; 
  }

  /* reduce size of avatar */
  .avatar-over {
    width: 38px;
  }
  .avatar-over a img {
    width: 36px;
    height: 36px;
    margin-top: 0;
    margin-left: 0;
  }
  
  /* change colour of quick reply boxes and remove whitespace */
 
  #qr_postform div.widget-content div.form-group {
    margin-bottom: 5px !important;
  }
  .form-control {
    background-color: rgb(70, 106, 152) !important;
    color: white;
  }

  /* REGISTER PAGE www.airliners.net/register/ */
  
  /* remove whitespace */
  .airliners-form-row {
    margin-bottom: 8px !important;
  }
  .airliners-form-row div label {
    margin-bottom: 0px !important;
  }
  .ul-form-inner form p {
    margin-bottom: 0 !important;
  }
  .layout-section.layout-section-max-600.user-login > div:nth-child(2) > div.ul-form-inner > form > div.text-right > button {
    background-color: rgb(61, 94, 127) !important;
    color: white !important;
  }
  #register_form_email_first,
  #register_form_email_second,
  #register_form_plainPassword_first,
  #register_form_plainPassword_second,
  #register_form_country,
  #register_form_username {
    background-color: rgb(61, 94, 127) !important;
    color: white !important;
  }
  
  /* LOGIN PAGE www.airliners.net/register/ */
  
  /* set colour and remove whitespace */
  .ul-form-inner {
    background-color: rgb(27, 55, 76) !important;
    margin-bottom: 0 !important;
    padding: 10px !important;
  }
  .ul-form-inner .row {
    margin-top: 10px !important;
  }
  .layout-section.layout-section-max-600.user-login > div:nth-child(2) > div.ul-form-inner > form > div:nth-child(3) > div.large-6.columns.text-right > button,
  #username,
  #password {
    background-color: rgb(61, 94, 127) !important;
    color: white !important;
  }
  
  /* ADD/EDIT REPLY/TOPIC www.airliners.net/forum/posting/ */
  
  /* change colour and borders */
  #postform > .post .well {
    border-color: black;
    border-style: solid;
    border-width: 2px;
    line-height: normal;
    margin-bottom: 4px;
    margin-top: 8px;
    padding: 5px;
  }
  #postform > .post .well,
  #postform > .post .content {
    background-color: rgb(27, 55, 76) !important;
    color: white;
    margin-top: 0;
    margin-bottom: 0;
  }
  #postform > .post .well h3{
    display: none;
  }
  #postingbox > div.panel-default,
  #postingbox > div.panel.has-nav-tabs.panel-default > ul,
  #postingbox > div.panel.has-nav-tabs.panel-default,
  #postingbox > fieldset,
  #postingbox > div.well {
    background-color: rgb(27, 55, 76) !important;
    border-color: black;
    border-style: solid;
    border-width: 2px;
    color: white;
    line-height: normal;
    margin-bottom: 8px;
    margin-top: 0px;
    padding: 5px;  
  }
  #page-body > h2 {
    font-size: 20pt;
    margin-bottom: 0 !important;
  }
  #postingbox > div.well > div > h3,
  #postform > h3 {
    font-size: 14pt;
    margin-bottom: 0 !important;
  }
  #postform > p {
    margin-bottom: 0 !important;
  }
  #postingbox > div.panel.has-nav-tabs.panel-default > ul > li > a,
  #postingbox > div.panel.has-nav-tabs.panel-default > ul > li > a > span {
    background-color: #d6d9e0 !important;
    color: black !important;
  }
  /* remove indentation of reply box and smilies */  
  #postingbox > div.well > fieldset > div.row > div.col-md-4 {
    padding-left: 0 ;
    padding-right: 0 ;
  }
  #postingbox > div.well > fieldset > div.row > div.col-md-8 {
    padding-left: 0 ;
    padding-right: 8px ;
  }
  /* remove whitespace on subject box */
  #postingbox > div.well > fieldset > div.control-group {
    margin-top: 0;
    padding-bottom: 2px;
  }
  #postingbox > div.well > fieldset > div.control-group > label {
    color: lightgrey;
    margin-bottom: 0;
  }
  #postingbox > div.well > fieldset > div.control-group > div > div {   
    padding-left: 2px;
    padding-right: 2px;
  }
  #postingbox > div.well > fieldset > div.control-group > div > div > #subject {   
    color: white !important; 
  }
  #qr_postform > div.widget-content > div:nth-child(3) {
    display: none;
  }
  .forum-answer .panel-footer {
    padding: 2px;
  }
  .forum-answer .panel-footer .text-center {
    margin-top: 2px;
    margin-bottom: 2px;
  }
  #qr_postform > div.widget-footer > div.form-group {
    margin-bottom: 0;
  }
  #postingbox > div.panel-default > div.panel-body {
    padding: 2px;
  }
  #postingbox > div.panel-default > div.panel-body > div.topicreview-content > div.panel {
    background-color: rgb(27, 55, 76) !important;
    border-color: black;
    color: white !important;
    line-height: normal;
    margin-bottom: 2px;
    padding: 4px;
  }
  #postingbox > div.panel-default > div.panel-body > div.topicreview-content > div.panel > h3 {
    display: none;
  }
  #postingbox > div.panel-default > div.panel-body > div.topicreview-content > div.panel > div.clearfix > div.pull-right > a {
    height: 20px;
    padding-top: 2px;
  }
  #preview {
    background-color: rgb(27, 55, 76);
    border-color: black;
    border-width: 2px;
    color: white;
    margin-bottom: 4px;
    padding: 2px;
  }
  #preview > h3 {
    display: none;
  }
  
  /* PHOTO DETAILS www.airliners.net/photo/ */
  
  /* remove whitespace at top of the page */
  .layout-main-content.layout-has-sidebar {
    padding-right: 308px !important;
    padding-top: 8px !important;
  }
  .layout-main-content-inner {
    padding-right: 0px !important;
  }
  
  /* remove whitespace between items in main panel */
  .pd-photo-module,
  .pd-section,
  .pd-photo-size-links {
    margin-bottom: 0px !important;
  }
  .pd-photo-module > div,  
  .pd-section > .layout-element {
    margin-left: 4px !important;
    margin-right: 4px !important;
  }
  .pd-photo-module > .layout-element {
    margin-left: 8px !important;
    margin-right: 8px !important;
  }
  .pd-section-title {
    margin-bottom: 0 !important;
  }
  .pd-section-view-more {
    margin-top: 0 !important;
  }
  .photo-details > div.layout-section > div.layout-element {
    margin-left: 4px !important;
    margin-right: 4px !important;  
  }
  .photo-details > .layout-section > .layout-element > .pd-section {
    margin-left: 0px !important;
    margin-right: 0px !important;  
    margin-bottom: 4px !important;
  }

  /* remove whitespace between items in sidebar panel */
  .layout-main-sidebar-inner {
    padding-top: 8px !important;
    margin-right: 8px !important;
  }
  .top-24-hours,
  .latest-forum-posts,
  .photographers-choice-sidebar,
  .top-100,
  .top-photographers,
  .desktop-placeholder_300x250_300x600_atf > div {
    margin-bottom: 8px !important;
  }
  .top-photographers.sidebar-module {
    margin-top: 8px !important;
  }
  .top-24-hours > h3 ,
  .latest-forum-posts > h3 ,
  .photographers-choice-sidebar > h3 ,
  .top-100 > h3 ,
  .top-photographers > h3 {
    margin-bottom: 0px !important;
  }
  
  /* remove whitespace within sidebar items */
  .sm-body {
    padding-bottom: 4px !important;
    padding-top: 4px !important;
  }
  .sm-body-top,
  .sm-more {
    padding-left: 4px !important;
    padding-right: 4px !important;
  }
  .sm-body-top > div {
    padding-bottom: 4px !important;
  }
  .sm-body-top > .forum-post {
    margin-bottom: 0 !important;
    margin-top: 0 !important;
  }

  /* add whitespace (yes, I know but it looks better) between photo and thumbnails */
  .pd-photo-module-photo {
  margin-bottom: 8px !important;
  }

  /* reduce spacing on the details lines */
  .pd-details-row > div {
    padding: 0px !important;
    line-height: normal;
  }
  .pd-details-row {
    border-bottom-style: dashed !important;
    border-bottom-color: lightslategrey !important;
  }

  /* change follow button on user profile panel */
  .ufb-link.button.ufb-follower-can-follow-followee {
    background-color: rgb(27, 55, 76);
  }

  /* change colours in comments section and remove whitespace */
  .fos_comment_submit > button {
    background-color: rgb(27, 55, 76);
    color: white;
  }
  .pd-comments-form-title,
  .pd-comments-form-fields {
    margin-bottom: 0 !important;
  }
  #fos_comment_comment_body {
    background-color: rgb(27, 55, 76) !important;
    color: white !important;
    padding-bottom: 4px !important;
    padding-left: 8px !important;
    padding-right: 8px !important;
    padding-top: 4px !important;
  }

  /* PHOTO SEARCH www.airliners.net/search/ */
  
  /* set colours*/
  .ps-v2-group {
    background-color: rgb(27, 55, 76) !important;    
  }
  .ps-v2-fields input,
  .selectize-input,
  .selectize-dropdown,
  .selectize-control {
    background-color: rgb(61, 94, 127) !important;
    background-image: none !important;
    color: white;
  }
  .optgroup-header {
    background-color: rgb(61, 94, 127) !important;
    color: lightgrey !important;
  }
  .selectize-dropdown-content div {
    color: white;
  }
  #photo_search_submit,
  #photo_search_submitExtra {
    background-color: rgb(61, 94, 127) !important;
    color: white !important;
    margin-bottom: 2px;
    margin-top: -2px;
  }
  
  /* remove whitespace*/
  .ps-v2-groups {
    margin-bottom: 4px !important;
  }
  .ps-v2-fields,
  .ps-v2-groups-header {
    margin-left: 0px !important;
    margin-right: 0px !important;
  }
  .ps-v2-results-row {
    margin-left: -4px !important;
