css:
@-moz-document url-prefix("https://rabb.it/") {
.desktop .roomLayout .mainRoom .videoChatView .vcWrapper>.leftSide>.lsWrapper>.mainContent>.focus .focusContainedView {
    background: #000;
}
.desktop .roomLayout .mainRoom .videoChatView .vcWrapper>.leftSide>.lsWrapper>.tray.screencast {
    background: #111;
}
#rabbitapp .toolbar .toolbarView {
    background-color: #111;
    box-shadow: 0 0 16px rgba(0, 0, 0, .8);
}
.socialLayout .trayWrapper .tray .desktopChatLayout {
    background: #111;
}
.socialLayout .trayWrapper .tray .desktopChatLayout .messages .conversationMessageCollectionView .conversationMessageView .messageBody {
    background-color: #111;
    border-radius: 0px;
    padding-top: 0px;
    padding-bottom: 0px;
    padding-left: 20px;
    padding-right: 0px;
    margin-right: -10px;
}
.socialLayout .trayWrapper .tray .desktopChatLayout .messages .conversationMessageCollectionView .conversationMessageView {
    color: rgba(255,255,255,.5);
}
.socialLayout .trayWrapper .tray .desktopChatLayout .footer>div>textarea {
    background-color: #1c1c1c;
}
.socialLayout .trayWrapper .tray .desktopChatLayout .messages .conversationMessageCollectionView .conversationMessageView div.userNameContainer {
    left: 20px;
    right: 30px;
}
.socialLayout .trayWrapper .tray .desktopChatLayout .messages .conversationMessageCollectionView .conversationMessageView {
    font-size: 16px;
    margin: 35px 30px 0 30px;
}
.socialLayout .trayWrapper .tray .desktopChatLayout .messages .conversationMessageCollectionView .conversationMessageView.isMe div.userPicture {
    left: -20px;
    right: -20px;
}
.socialLayout .trayWrapper .tray .desktopChatLayout .messages .conversationMessageCollectionView .conversationMessageView div.userNameContainer div.userName {
    float: left;
    color: rgba(255,255,255,.8);
    font-size: 16px;
    top: 0px;
}
.socialLayout .trayWrapper .tray .desktopChatLayout .messages .conversationMessageCollectionView .conversationMessageView div.userNameContainer time {
    float: right;
    top: 1px;
    right: -50px;
    font-size: 14px;
    color: #111;
}
.socialLayout .trayWrapper .tray .desktopChatLayout .messages .conversationMessageCollectionView .conversationMessageView div.userNameContainer time:hover {
    float: right;
    top: 1px;
    right: -50px;
    font-size: 14px;
    color: rgba(255,255,255,.8);
}
.socialLayout .trayWrapper .tray .desktopChatLayout .messages .conversationMessageCollectionView .conversationMessageView.isMe .messageBody {
    float: left;
}
.socialLayout .trayWrapper .tray .desktopChatLayout .messages .conversationMessageCollectionView .conversationMessageView div.userPicture {
    width: 30px;
    height: 30px;
}
.socialLayout .trayWrapper .tray .desktopChatLayout .messages .conversationMessageCollectionView .conversationMessageView .media {
    background-color: #1c1c1c;
    border-radius: 0px;
}

