css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("thomann.de") {
  /* DO NOT SHOW BACKGOUNDPICTURE */
  .lr-page-wallpaper-wrapper {
    display: none;
  }
  
  /* SET GADIENT ON THE LEFT TO BLACK*/
  body {
    background: /*[[bg-color]]*/!important;
  }
  
  /* REMOVE LEFT MARGIN */
  .lr-page {
    margin: 0!important;
  }
  
  /* CENTER CONTENT */
  .lr-page-content-wrapper {
    width: 500px;
    display: block;
    margin: 0 auto!important;
  }
}
