css:
@-moz-document url-prefix("http://aviacreations.com/modme/forum/topic.php"), url-prefix("http://aviacreations.com/modme/forum/usermessage.php") {
/* Styles by Mrpeanut188 */
/* Version 3, published August 19, 2016 */

/*[[mod]]*/

/* Margin on username */
div p.left {
    margin: 0px 0px 0px 0px !important;
}

/* Poster Info Box */
.user-information-post {
    width: 140px !important;
    min-width: 140px !important;
}

/* Profile Info Box */
div.col.s12.m5.l3 {
    width: auto !important;
}

/* Poster Info  */
div[class*="user-information-post"] div {
    width: 135px !important;
    height: auto !important;
    text-align: center;
}

/* Profile Link */
div[class*="user-information-post"] div p {
    /* Can't clear float, increase width to 100% and center text */
    width: 100%;
    text-align: center;
}

/* Give Win Button */
button[class*="user-give-win"] {
    margin-bottom: 15px !important;
}

/* Darken quote background for better visibility */
blockquote.quote-block {
    background-color: #262626;
}

/*[[signatures]]*/

/*Remove Separator*/
.post-separator
{
    height: 0px !important;
}

}

