@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("workflowy.com") {

/* HOW TO USE: Zoom in to the CSS bullet point containing the CSS as a Note, and then run the extension script "stylenote" in Handyflowy */

/* Regular(name) nodes */

.name .content .contentTag {
  color: rgba(83, 131, 203, 1);
  background: rgb(242, 242, 242);
}
.name .content .contentUnderline {
  text-decoration: inherit;
  background-color: rgba(255, 170, 204, 0.74);
  color: black !important;
}
.name u {
  text-decoration: underline;
  background-color: rgba(255, 170, 204, 0.74) !important;
  color: black !important;
}
.name .content .contentLink {
  color: rgba(197, 172, 1, 0.7);
  background-color: transparent;
}
.name .content .contentTag .contentUnderline {
  background-color: inherit;
}
.name .content .contentLink .contentUnderline {
  background-color: inherit;
}
.name .contentTag {
  background: rgb(242, 242, 242);
}
.name .content .contentTag u {
  background-color: inherit !important;
}
.name .content .contentLink u {
  background-color: inherit !important;
}

/* DEL? */
.contentTagText {
  text-decoration: none !important;
}

