css:
@-webkit-document domain(youtube.com) {
/* 
 * Name:    BlurTube v1
 * Author:  RetrBlade
 * Version: v1.2
 */
#yt-masthead-container, #guide-container
{
	background-color: rgba(255, 255, 255, 0.5)!important;
	-webkit-backdrop-filter: saturate(180%) blur(20px);
	backdrop-filter: saturate(180%) blur(20px);
	background-image: none !important;
}
#appbar-guide-menu
{
	background-color: rgba(255, 255, 255, 0.5)!important;
	-webkit-backdrop-filter: saturate(180%) blur(20px);
	backdrop-filter: saturate(180%) blur(20px);
	background-image: none !important;
}
.ytp-gradient-bottom, .html5-video-player, ::-webkit-scrollbar, .ytp-gradient-top, .ytp-gradient-bottom, .ytp-playlist-menu, .ytp-playlist-menu-header, .ytp-panel-menu, .ytp-settings-menu
{
	background-color: rgba(255, 255, 255, 0.5)!important;
	-webkit-backdrop-filter: saturate(180%) blur(20px);
	backdrop-filter: saturate(180%) blur(20px);
	background-image: none !important;
}
.ytp-gradient-bottom
{
	height: 40px !important;
	background-image: none !important;
}
.ytp-big-mode .ytp-gradient-top
{
	height: 80px !important;
	background-image: none !important;
}
.ytp-big-mode .ytp-gradient-bottom
{
	height: 60px !important;
	background-image: none !important;

}
.ytp-gradient-top
{
	background-image: none !important;
}
.ytp-popup
{
	background-color: rgba(255, 255, 255, 0.5) !important;
}
.ytp-gradient-bottom
{
	border-top: 1px solid black;
}
.ytp-gradient-top
{
	border-bottom: 1px solid black;
}
}
