css:
@-moz-document url("http://orteil.dashnet.org/cookieclicker/") {
/*Customize!*/

/*Gold Cookie opacity and Store sizes)
/*[[goldop]]*/
/*[[storesize]]*/
.product .content .owned
{ display: inline-block; top: 0; font-size: 25px; }

/*Fonts!*/
body, div, p, a, h1
{ font-family: '/*[[font]]*/' !important; }

/*Bars!*/
/*[[bartar]]*/

/*Screen settings*/
/*[[screen]]*/

/*White borders for buttons!*/
a.option, textarea, input[type="text"], .sliderBox, .smallFramed, .framed
{ border: 1px solid #e2dd48; border-color: #fff #fff #fff #fff !important; border-radius: 1px; }

/*Moves notes*/
#notes
{ position: absolute; left: 7.20%; margin-left: -125px; bottom: 0px; z-index: 100000000; }

#notes .remaining
{ padding: 3px; opacity: 0.75; text-shadow: 0px 0px 2px #000, 0px 1px 0px #000; }

.note
{ position: relative; overflow-y: hidden; width: 300px; padding-right: 16px; padding-bottom: 8px; left: -18px; }

.commentsText
{ padding: 0px 0px; position: absolute; top: 0px; left: 0px; right: 0px; opacity: 1; text-align: center; font-size: 0px; }

/*Removes the top bar*/
#topBar
{ position: absolute; left: 0px; top: 0px; width: 0%; height: 0px; color: #ccc; font-size: 0px; }

#game
{ position: absolute; left: 0px; top: 0px; right: 0px; bottom: 0px; overflow: hidden; }
}
