css:
@-moz-document url-prefix("https://zhidao.baidu.com/question"), url-prefix("http://zhidao.baidu.com/question") {
/*屏蔽不需要的内容*/

[class*="ads"],
[class*="ad-block"],
aside,
i.menu-new,
.wgt-eva,
.ec-oad,
.word-replace,
.wgt-footer-new,
.wgt-relate-search,
.wgt-bottom-union,
.jump-top-box,
.shop-entrance,
.wgt-silder-push,
.close-question-tip,
.wgt-push-intro.smooth,
.question-all-answers-number,
.has-menu .search-box-new .channel,
#header>a,
#qb-content [class*='-more'],
#qb-content .related-exp,
#qb-content>div i,
#qb-content table i,
#qb-content>div ul li em,
#userbar-msg,
#qb-side,
#deom {
    display: none!important;
}
}

@-moz-document url-prefix("https://zhidao.baidu.com/question"), url-prefix("http://zhidao.baidu.com/question") {
/* 排版 */

#body{
    margin-bottom:50px;
}

a {
    text-decoration: none!important;
}
.search-box-new{
    height:55px;
}


.qb-section{
    width: 1000px;
    margin: auto;
}
.has-menu .search-box-new .search-cont {
    width: 730px!important;
}
.wgt-header-title .wgt-header-title-content {
    
    width: 1000px;
   
}
.wgt-header-title-btn{
    float:right;
    margin-top:10px;
}

#qb-content {
    padding: 0;
    width: 100%;
    border-radius: 5px;
    box-sizing: border-box;
}
#qb-content>div {
    padding: 0;
    border: none;
    margin: 10px auto;
}
#qb-content>div:first-child {
    margin-top: 0;
}
#qb-content>div:last-child {
    margin-bottom: 0;
}
/* 问答 */

.wgt-ask {
    padding: 10px 20px!important;
}
/* 最好答案 */
div.wgt-best .hd{
    margin-top:0!important;
}
.wgt-best .hd {
    padding: 7px 10px;
}
.wgt-best .bd {
    padding: 0 20px;
}
.wgt-best .wgt-replyer-special-bg {
    border: none;
}
/* 其它回答 */

.wgt-answers {
    padding-bottom: 20px !important;
}
.wgt-answers .hd h2 {
    padding: 10px 20px;
}
.wgt-answers .bd-wrap {
    border: none;
}
.wgt-answers .bd {
    border-bottom: 1px dotted #e9e9e9;
    padding: 24px 20px 0px;
}
.wgt-answers #show-fold-container,
.wgt-answers #show-hide-container,
.wgt-answers .show-answer-dispute {
    height: 40px;
    line-height: 40px;
    margin: 15px 420px 5px;
    border-radius: 60px;
}
.wgt-answers #show-answer-fold,
.wgt-answers #show-answer-hide,
.wgt-answers .show-hide-dispute {
    cursor: pointer;
    font-size: 16px;
}
.wgt-answers #show-answer-fold.hover,
.wgt-answers #show-answer-hide.hover,
.wgt-answers .show-hide-dispute.hover {
    border-radius: 60px;
    text-decoration: none;
}
/* 列表 */

.wgt-related,
.wgt-topic,
.wgt-related .related-list,
.wgt-push {
    padding: 0!important;
}
.wgt-silder-push u1,
.wgt-related .related-list {
    margin: 0;
}
.wgt-topic h2,
.wgt-related h2,
.wgt-push h2 {
    padding: 10px 20px;
}
.wgt-topic ul,
.wgt-related ul,
.wgt-push ul,
.wgt-topic .topic-list,
.wgt-push .push-ul {
    margin: 0px;
    padding: 0px;
}
.wgt-topic ul li,
.wgt-related ul li,
.wgt-push ul li {
    padding: 5px 20px;
}
.wgt-topic ul li:hover a,
.wgt-related ul li:hover span,
.wgt-push ul li:hover span,
.wgt-push ul li:hover a {
    font-weight: bold;
}
/* 调节 */

#qb-content .wgt-push,
#qb-content .push-ul,
#qb-content .wgt-related,
#qb-content .related-list,
#qb-content .wgt-topic,
#qb-content .topic-list {
    margin-top: 0;
    padding-top: 0;
}
#qb-content .wgt-push,
#qb-content .push-ul {
    max-height: 500px;
}

.wgt-best-operator{
    padding-bottom:10px;
}


.f-aid,
a.f-aid {
    color: #333;
}
body .pager a:hover {
    color: #fff;
    border: solid 1px #43cc69;
}


/* 修复排版错位 */
.accuse-area .wgt-accuse {
   
    line-height: 1;
    
}

#answer-bar {
    line-height: 30px;
    
}

.wgt-ask .ask-info .wgt-accuse {
   
    top: 9px;
    
}
.wgt-replyer-all {
    margin-left: 20px;
}
}

@-moz-document url-prefix("https://zhidao.baidu.com/question"), url-prefix("http://zhidao.baidu.com/question") {
/* 背景和颜色 */

body {
   
    background-color: #f1f2f3;
    background-size: cover;
    /*     filter:blur(5px); */
}
#qb-content>div {
    background: #fff;
}
/* 问答 */

.wgt-ask:hover {
    background: #f5f5f5;
}
/* 最佳答案 */

.wgt-best .hd {
    background: #27ae61;
}
.wgt-best .hd span {
    color: #fff;
}
.wgt-best .wgt-replyer-special-bg {
    background: rgba(218, 224, 228, 0.21);
}
div.wgt-best .answer-title{
    font-size:20px;
    width:100px;
}
/* 其它回答 */

.wgt-answers .hd h2 {
    background: #2aa198;
}
.wgt-answers .hd h2 {
    color: #fff;
}
.wgt-answers .bd:hover {
    background: #f8f8f8;
}
.wgt-answers #show-fold-container,
.wgt-answers #show-hide-container,
.wgt-answers .show-answer-dispute {
    background: #46b39e;
    color: #fff;
}
.wgt-answers #show-answer-fold,
.wgt-answers #show-answer-hide,
.wgt-answers .show-hide-dispute {
    color: #fff;
}
.wgt-answers #show-answer-fold.hover,
.wgt-answers #show-answer-hide.hover,
.wgt-answers .show-hide-dispute.hover {
    background: #1bbc9b;
    color: #fff;
}
/* 列表 */

.wgt-topic h2,
.wgt-related h2,
.wgt-push h2 {
    background: #95a5a5;
}
.wgt-topic h2,
.wgt-related h2,
.wgt-push h2,
.wgt-topic h2 em {
    color: #fff;
}
.wgt-topic li:nth-child(even),
.wgt-related ul li:nth-child(even),
.wgt-push ul li:nth-child(even) {
    background: #f9fafb;
}
.wgt-topic ul li:hover,
.wgt-related ul li:hover,
.wgt-push ul li:hover {
    background: #f5f5f5;
}
.wgt-topic .topic-list li:hover a,
.wgt-push .push-ul li:hover a,
.wgt-topic ul li:hover a,
.wgt-related ul li:hover span,
.wgt-push ul li:hover span,
.wgt-push ul li:hover a {
    color: #333;
}
.wgt-topic ul li span,
.wgt-related ul li span,
.wgt-push ul li span {
    color: #333;
}
/* 调节 */

.f-aid,
a.f-aid {
    color: #333;
}
body .pager b {
    background: #2aa198;
}
body .pager a:hover {
    color: #fff;
    background: #34bab0;
}
}

@-moz-document url-prefix("https://zhidao.baidu.com/question"), url-prefix("http://zhidao.baidu.com/question") {
/* 特效 */

#qb-content>div {
   border: 1px solid #d8d8d8;
}
}

@-moz-document url-prefix("https://zhidao.baidu.com/search"), url-prefix("http://zhidao.baidu.com/search") {
.wgt-bottom-ask,
.aside.fixheight,
.shop-entrance,
div[class*="_zwd_"] {
    display: none!important;
}
}
