css:
/* 
Name:    Viki Black-Silver Edition
Author:  Elkisab
Version: 1.4 (Aug.4.16)
*/

/*Début*/
@-moz-document domain("viki.com") {
/*Modif de Background*/
body {

background-image: url(/*[[background]]*/)!important;
background-attachment:fixed;
    
}
/*Apparence Général du Site*/
.page-header {
    background-color : #DCDCDC
}
.navbar-primary {
    background-color : #000;
}
.menubar {
    background-color : #000;
}
.card-content {
    background-color : #DCDCDC;
}
.card-action {
    background-color : #DCDCDC
}
.modal-content {
    background-color: rgba(0, 0, 0, 0);
}
.cinema-background {
    opacity : 0
}
.page-container{
    background-color:#dcdcdc
}
/*Suppression de Diverses Sections*/
.page-footer {
    display : none;
}
.page-colophon-fr {
    display : none
}
.breadcrumb-header{
    display : none
}
/*Autre Modifs*/
.pull-right {
    color : black
}
.uppercase {
    color : darkred
}
.content-link {
    color : darkred
}
.section-heading {
    color : darkred
}
.info {
    color : darkred
}
.emphasis {
    color : darkred
}
.page-link {
    color : darkred
}
.page-link-current {
    color : darkred
}
.display-1{
    color : darkred
}
/*Modif de la Barre de Recherche*/
.navbar-expanded .navbar-search-container{
    width:200px;    
}
.navbar-search-form{
    color:#fff;
    background-color:black;
    position:relative
}
.navbar-search-form input.navbar-search-box{
    border-bottom:0px solid #fff
}
.navbar-search-form input.navbar-search-box:focus:not([readonly]){
    border-bottom:0px solid #fff
}
/*Modif des Badges*/
.badge-premium{
    background-color:darkred;
    text-shadow:none
}
.badge-accent{
    background-color:#800080
}
.moonshine .badge-accent{
    background-color:#800080
}
.badge-primary,.badge-dark,.badge-watch-marker{
    background-color:black
}
/*Modif de l'affichage du Code CSS du site*/
pre {
    background-color : black;
    color : darkgreen
}
/*Modif de la Section Viki Discution*/
#main-outlet {
    background-color : #dcdcdc
}
.d-header{
    left:0;
    z-index:1000;
    padding-top:3px;
    height:60px;
    background-color : black
                        
}
.d-header #site-logo{
    display : none
}
/*Modif Page Support Viki*/
.header {
    background-color : black;
}
.dropdown-toggle {
    color : white
}
/*diverses modif de couleur pour cause de illisibilité*/
.private-message-glyph{
    color:black;
}
nav.post-controls a,nav.post-controls button{
    color:black
}
.small-action .topic-avatar i{
    color:black
}
.small-action .small-action-desc{
    color:black
}
}
/*Fin*/
