css:
@-moz-document domain("www.giantitp.com") {
  body {
    background-color: /*[[bgcolor]]*/;
  }
  
  body > table > tbody > tr:first-child {
    display: none;
  }
  
  body > table > tbody > tr > td > table > tbody > tr > td:first-child {
    display:none;
  }
  
  body > table > tbody > tr > td > table > tbody > tr > td > table {
    width:100%;
  }
  
  table {
   margin: auto;
  }
  
  body > table > tbody > tr > td > table > tbody > tr > td > table > tbody > tr > td > center{
    display:none;
  }
}
