css:
@-moz-document domain("www.bloxcity.com") {

.sp-picker-container {
    width: /*[[size]]*/!important;
    /* Change this number if you want it smaller (e.g. 350px) */
}

.sp-dragger {
    height: /*[[bdot]]*/!important;
    width: /*[[bdot]]*/!important;
    /* Change these numbers if you want to make the color dot picker smaller (e.g. 8px) */
}
/* You can change these freely, if you want. */
.sp-picker-container {
    background: #FFFFFF!important;
     text-transform: capitalize;
    box-shadow: 0 1px 2px #ccc!important;
}
.sp-choose {
    border: 0!important;
    background: #C03!important;
    color: white!important;
    padding: 6.6px 6px!important;
    font-size: 12px!important;
    font-weight: 600!important;
    text-decoration: none!important;
    cursor: pointer!important;
}
.sp-button-container .sp-cancel {
    border: 0!important;
    background: #C03!important;
    color: white!important;
    padding: 5px 8px!important;
    font-size: 12px!important;
    font-weight: 600!important;
    text-decoration: none!important;
    cursor: pointer!important;
        text-transform: capitalize;
    
}
.sp-container button {
    background-color: #00a100!important;
    background-image: linear-gradient(to bottom, #00a100, #00a100)!important;
    border-bottom: none!important;
    color: #fff!important;
    padding: 6.6px 6px!important;
    text-align: center!important;
    text-shadow: none!important;
    
    font-size: 12px!important;
    font-weight: 600!important;
    text-decoration: none!important;
    border: none!important;
        text-transform: capitalize;
    border-radius: 0%!important;
}
.sp-container button:hover {
      background: #038703!important;
      color: white!important;
      text-decoration: none!important;
      border: none!important;
        text-transform: capitalize;
}
.sp-preview-inner {
    transition: all .3s ease-out!important;
}
.sp-cancel:hover {
     background: #A82A00!important;
     color: white!important;
     text-decoration: none!important;
        text-transform: capitalize;
}
}
