css:
@-moz-document domain("vk.com") {
	body {
    font-family: tahoma, arial, verdana, sans-serif, Lucida Sans;
}

#page_body {
    font-size: 12px;
}

.wall_module .reply_img, .module_body .people_cell_img, .page_list_module .cell_img, .page_list_module .thumb {
    border-radius: 0px; 
}

.audio_row .audio_performer, .post_author .author, .wall_module .author, .wall_module .copy_author {
    font-weight: 700;
}

.audio_row .audio_info, .wall_module .reply_author {
    font-size: 12px;
}
}
