css:
@-moz-document regexp(".*myanimelist\\.net/(?!animelist|mangalist).*") {
/*
Install with the Stylish add-on.
By Kiuseii @MyAnimeList
If you need help contact me :
http://myanimelist.net/profile/Kiuseii
*/ 


/*Pseudo background and Pseudo hover background color */
.page-common .header-profile.link-bg {
background-color: rgba(0,0,0,0);
}

.page-common .header-profile.link-bg:hover {
background-color: rgba(0,0,0,0);
}

/*To change the color of Icons and Pseudo Text:*/

.page-common .header-profile-link {
color: /*[[Icon-color]]*/;
}

.page-common .header-list-button {
color: /*[[Icon-color]]*/;
}

.page-common .header-message-button {
color: /*[[Icon-color]]*/;
}

.page-common .header-message-button:visited {
color: /*[[Icon-color]]*/;
}

.page-common .header-notification-button {
color: /*[[Icon-color]]*/;
}

/*To change the hover color of Icons and hover Pseudo Text:*/

.page-common .header-profile-link:hover {
color: /*[[Hover-color]]*/;
}

.page-common .header-list-button:hover {
color: /*[[Hover-color]]*/;
}

.page-common .header-message-button:hover {
color: /*[[Hover-color]]*/;
}

.page-common .header-notification-button:hover {
color: /*[[Hover-color]]*/;
}

/*To change the color of background and Pseudo text when he is selected*/

.page-common .header-profile.link-bg.on {
color: /*[[Hover-color]]*/;
background-color: rgba(0,0,0,0);
}

.page-common .header-profile.link-bg.on:hover {
color: /*[[Hover-color]]*/;
background-color: rgba(0,0,0,0);
}
}
