description:
UPD 6/02/17 Стиль для комфортного чтения в темное время суток.

Картинки затемнены по-умолчанию, чтобы не бить ярким белым цветом в глаза. Нормальную окраску принимают по наведению мыши;
Несколько тем;

css:
@-moz-document domain("vc.ru") {
:root {
/*[[colorchoice]]*/
/*[[img]]*/

/*----------------------------------------------------------------------------------------------------*/
/*----------------------------------------------БРЕЙК-------------------------------------------------*/
/*----------------------------------------------------------------------------------------------------*/

.author-info, .b-articles-extra__profile__e, .b-c-questions__item__profile__e, html:not(.m-tablet) .b-articles-p-w_black .b-articles-p__more { background: none !important; }
/*Убираем фон*/

#foot, #navbar .nav_tabs_content .nav_tab .line, #layout .inner, .edit-setting, html, div.nubbin>div.spacer, div.nubbin, article.project_card, div.sheet.person.overview>div.sheet_body, aside.tools, section.comments .comment, div.sheet, .footer, body, .b-story__header, .pg_click, .b-comments_new-line:before, .b-compare-item_is_part .b-compare-item__content-wrapper, .b-articles-p-w_transparent .b-articles-p__title .z-title, .b-articles-p-w_transparent:before, .b-comment__rating.negative, .b-comment__rating.positive, .b-content-w-comments_live, .b-page-leak__form, .comments_list .comment_item .message.bad5, body.nl .layout, .b-last-comments, .b-popular__more, .b-popular__title, .b-news__content, .b-feed-pagination, .b-aside-block-widget
{ background: var(--color1) !important; }
/*Цвет фона*/

.b-comments-sort__btn .sortButton.active, .b-comment__user, .b-articles__b__title__desc, .b-articles-p__title, .b-ph-default, .b-popup-login p, .b-userinfo__username, .b-sponsored__title
{ color: var(--color5) !important; border-bottom: none !important; text-shadow: none !important; }

.b-save-story-box>div.ss-cats>div.save-story-cat>i, .b-comment__text p, body { color: var(--color5) !important; border-color: var(--color5) !important; }
/*Цвет текстов*/


.b-article__infopanel-bottom .z-favorite, .b-article__infopanel-bottom .z-comments, .b-articles-p-w_transparent .b-articles-p__more, .commentWrapper, .b-comments__headline, .b-comment-add, .b-articles-p-w_black, .b-main-w, .b-section-articles, .b-section-extra, .b-popup-login, .b-userinfo__username__qrcode p, .b-user-profile, .b-user-profile-notes, body.nl .layout__navbar, .b-main .b-nav, .b-feed-item, .b-feed-item__comments, .b-sponsored, .b-article-old .b-main-article blockquote.q-2, .b-article-old .b-main-article blockquote.q-1, .b-article-old .b-main-article blockquote.q-3
{ background: var(--color2) !important; }
/*Цвет подложек*/

.adsmenu, .b-gtpost, .b-compare__empty, .b-tag-search, .b-compare, .b-compare__head { border: 1px solid var(--color3) !important; color: var(--color5) !important; line-height: 150% !important; text-shadow: none !important }
/*Параметры подложек*/

.b-comments_new-line:before, .b-articles-p__more, .b-article article blockquote.q-1 { background-color: var(--color4) !important; background: var(--color4) !important; box-shadow: none; }


a, .b-comment__control, .b-story-info__set-order, .b-comment__content-toggle, a.menu-unsubscribe-btn, .b-story__show-all span, .b-section-articles__btn-more, .b-articles-p__more, .b-comment__article-name
{ color: var(--color7) !important; transition: 0.2s !important; text-shadow: none}

a:hover, .b-comment__content-toggle:hover, a.menu-unsubscribe-btn:hover, .b-story__show-all:hover span, .b-section-articles__btn-more:hover, .b-comments__live-link:hover~.b-comment__wrapper .b-comment__article-name
{ color: var(--color8) !important; transition: 0s !important;}
/*Цвет ссылок*/

a, .b-article__related__b .z-name, .b-comment__article-name u, a u
{ border-bottom: 1px solid var(--color10) !important; transition: 0.2s; text-decoration: none !important; }

a:hover, .b-article__related__b .z-name:hover, .b-comments__live-link:hover~.b-comment__wrapper .b-comment__article-name u, a:hover u
{ border-bottom: 1px solid var(--color9) !important; transition: 0s; text-decoration: none !important; }
/*Оформление ссылок*/

a.b-comments__live-link, a.b-articles-p__b__title, a.b-articles-comments, a.b-articles__b__title, a.z-link, a.vk, a.fb, a.tw, u, a.b-userinfo__username__exit, a.b-comments__live-link, a.b-articles-p__b__title, a.b-articles-comments, a.b-articles__b__title, a.z-link, a.vk, a.fb, a.tw, u, a.b-userinfo__username__exit, .b-paper-header__persons a
{ border-bottom: none !important; }
/*Убираем подчеркивание*/

.b-related-stories>a.rs-more-btn { border-bottom: 1px dotted var(--color10) !important; text-decoration: none !important; }
.b-related-stories>a.rs-more-btn:hover { border-bottom: 1px dotted var(--color9) !important; transition: 0s; text-decoration: none !important; }
/*Оформление псевдоссылок*/

nav ul li a { border-color: var(--color4) !important; color: var(--color5) !important; background: var(--color2) !important; text-shadow: none !important; transition: 0.2s; box-shadow: none !important; border-radius: 3px; vertical-align: middle !important;}

nav ul li a:hover { background: var(--color9) !important; color: var(--color8) !important; border-color: var(--color9) !important; transition: 0s;}
/*Кнопки и тэги*/

.tags-bar>div.tb-tag>div:hover { background-color: var(--color9) !important; color: var(--color8) !important; border-color: var(--color9) !important; transition: 0s;}



.b-btn, .b-page-leak__submit, .b-userinfo__soc, .b-comment-add__submit__send
{background: var(--gc) !important; border: none !important; color: var(--color1) !important; text-shadow: none; transition: 0.2s; box-shadow: none}

.b-btn:hover, .b-page-leak__submit:hover, .b-userinfo__soc:hover, .b-comment-add__submit__send:hover
{background: var(--gc) !important; border: none !important; color: var(--color1) !important; transition: 0s; -webkit-filter: saturate(1.5) brightness(1.1); box-shadow: 0 7px 36px -8px var(--gc);}
/*Цвет зеленых кнопок*/

.b-comment-add__message textarea, .b-comment-add__message textarea:hover, .b-comment-add__message textarea:focus, a.b-comments__live-link, a.b-articles-p__b__title, a.b-articles-comments, a.b-articles__b__title, a.z-link, a.vk, a.fb, a.tw, u, a.b-userinfo__username__exit, .b-paper-header__persons a { border: none; border-bottom: none !important; }
/*Убираем границы*/

.b-comment-toggle_type_expand .b-comment-toggle__icon, .b-content__sidebar { display: none }
/*Отключаем элементы*/


.b-comment-add__message, .editor .text-holder, textarea, section.comments .comment.new header.text_entry, section.comments .comment.edit header.text_entry, div.edit_taggings div.expanded_content input[type=text], div.jumpto input[type=text], #search-terms, input, .b-communities .community-search .cs-text>input, .b-communities .community-search .cs-text, .uical>.uical-head>div.uical-date>input, .note_wrap > input[type="text"], .story-search div.search-text-field>input, .b-comments-reply__editable, .tags-bar>div.tb-tag, .b-saved-stories-panel .ss-search div.ss-text>input, .add_story_cn_textarea, input[type="password"], .search-text-field, #search_menu, input.add_story_cn_textarea.comm-search-inp, input[type="password"], .selectize-input 
{border: 1px solid var(--color4) !important; background: var(--color1)!important; transition: 0.2s; color: var(--color5)!important; box-shadow: none !important }

.b-comment-add__message:hover, .editor .text-holder:hover, textarea:hover, section.comments .comment.new header.text_entry:hover, section.comments .comment.edit header.text_entry:hover, div.edit_taggings div.expanded_content input[type=text]:hover, div.jumpto input[type=text]:hover, input:hover, #search-terms:hover, .b-communities .community-search .cs-text>input:hover, .uical>.uical-head>div.uical-date>input:hover, .story-search div.search-text-field>input:hover, .b-comments-reply__editable:hover, .tags-bar>div.tb-tag:hover, .b-saved-stories-panel .ss-search div.ss-text>input:hover, .add_story_cn_textarea:hover, input[type="text"]:hover, input[type="password"]:hover, textareaLhover, .search-text-field:hover, #search_menu:hover, input.add_story_cn_textarea.comm-search-inp:hover, input[type="password"]:hover, textarea:hover, .b-gtpost-builder__input:hover, .selectize-input:hover 
{border: 1px solid var(--color8) !important; transition: 0s; }

.b-comment-add__message:focus, .editor .text-holder:focus, textarea:focus, section.comments .comment.new header.text_entry:focus, section.comments .comment.edit header.text_entry:focus, div.edit_taggings div.expanded_content input[type=text]:focus, div.jumpto input[type=text]:focus, input:focus, #search-terms:focus, .b-communities .community-search .cs-text>input:focus, .uical>.uical-head>div.uical-date>input:focus, .b-save-story-box>div.save-story-new-cat input:focus, .story-search div.search-text-field>input:focus, .b-comments-reply__editable:focus, .tags-bar>div.tb-tag:focus, .b-saved-stories-panel .ss-search div.ss-text>input:focus, .add_story_cn_textarea:focus, input[type="text"]:focus, input[type="password"]:focus, textarea:focus, div.search-text-field.ss-selected:focus, #search_menu:focus, input.add_story_cn_textarea.comm-search-inp:focus, input[type="password"]:focus, textarea:focus, .b-gtpost-builder__input:focus, .selectize-input:focus 
{border: 1px solid var(--color8) !important; background: var(--color1)!important; box-shadow: rgba(0, 0, 0, .3) 0 1px 4px !important; color: var(--color5)!important; transition: 0.2s}
/*Поле ввода текста*/
.b-comment__rating__marks .list .wrap-list ul li, .b-content-w-comments_live, .b-news-top, .b-menu-tabs, .editor .editor__footer, .tracker_page table.tracker_folowers tr td.event_type, .tracker_page table.tracker_folowers tr td, .dropdown-menu, .b-last-comments__item, .b-last-comments__title, .b-article-old .b-main-article img.framed, .b-popular, .b-nav, .b-feed-item__footer
{ border-color: var(--color3) !important; }
/*Затемнение бордеров*/

/*VC.ru*/
.b-header {position: absolute; background: var(--color2) !important}
.b-last-comments:after, .b-feed-more {background: linear-gradient(0deg, var(--color1), rgba(255,255,255,0));}



.b-comments-sort__btn .sortButton.active:before, .b-comments-sort__btn .sortButton.active:hover:before {background: var(--color9) !important}
.b-comments-sort__btn .sortButton.active u {border-bottom: 1px dotted var(--color10);}
.b-comment__rating__marks:after {border-color: var(--color3) transparent transparent;}
.b-comment-add {padding-left: 56px; padding-right: 50px; margin: 0px 0 0;}
.b-content-w-comments_live {padding-top: 0px;}
.b-comments {border: none !important}
.b-articles-p-w_black .b-articles-p__more:hover {border: 2px solid var(--color8); color: var(--color8); opacity: 1}
.b-articles-p-w_black .b-articles-p__more i {color: var(--color7);}
.b-section-articles__btn-more {
	background: linear-gradient(to bottom,rgba(238,246,247,0) 0, var(--color4) 100%);}
.b-section-articles__btn-more:hover {
	background: linear-gradient(to bottom,rgba(238,246,247,0) 0, var(--color9) 100%);}
.b-content, .b-content-w {background: var(--color2)}
.b-content-w-comments_live {background: var(--color1)}
.b-ph-default, .b-comment.highlight, .b-comment__rating__marks .list, .b-comment__rating__marks .list .wrap-list ul li a i:after {background: var(--color3)}
.b-ph-default::before {border-color: transparent var(--color1) var(--color4) transparent!important;}
a.vk, a.fb, a.tw {color: #fff !important}
.b-page-leak__form::before {border-color: transparent var(--color2) var(--color3) transparent!important;}
.b-page-leak__form span i {background-color: var(--color8); color: var(--color1); border: 4px solid var(--color1);}
.b-articles-p-w_transparent {box-shadow: 0 0 0 1px var(--color3);}

::-webkit-scrollbar { width: 10px !important; height: 10px !important;}
::-webkit-scrollbar-thumb { background: var(--color7) !important;}
::-webkit-scrollbar-track { background: var(--color1) !important;}
::-webkit-scrollbar-track-piece { background: var(--color2) !important;}
blockquote, .b-articles-p-w, .b-article article blockquote.q-2 { background: var(--color4); }
.b-articles-p-w_transparent {background: var(--color1); }
.b-comments-sort__btn .sortButton:before {background: var(--color7); }
.b-comments-sort__btn .sortButton:hover:before {background: var(--color8); }
}
