css:
@-moz-document domain("upsb.info") {
a:link{color:#000000 !important;}
a:visited{color:#000000 !important;}
a:hover{color:#000000 !important;}
a:active{color:#000000 !important;}

 
html {
     Background-color: /*[[Color2]]*/ !important;
    overflow-y: scroll !important;
}
 
.navTabs {
    font-size: 12px;
    Background-color: /*[[Color]]*/ !important;
    padding: 0 5px;
    border-bottom: 1px solid rgb(144, 152, 167);
    -webkit-border-top-left-radius: 3px;
    -moz-border-radius-topleft: 3px;
    -khtml-border-top-left-radius: 3px;
    border-top-left-radius: 3px;
    -webkit-border-top-right-radius: 3px;
    -moz-border-radius-topright: 3px;
    -khtml-border-top-right-radius: 3px;
    border-top-right-radius: 3px;
    background-image: -moz-linear-gradient(top, rgba(255,255,255,0.07) 0%, rgba(255,255,255,0) 100%);
    background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(255,255,255,0.07)), color-stop(100%,rgba(255,255,255,0)));
    background-image: -webkit-linear-gradient(top, rgba(255,255,255,0.07) 0%,rgba(255,255,255,0) 100%);
    background-image: -o-linear-gradient(top, rgba(255,255,255,0.07) 0%,rgba(255,255,255,0) 100%);
    background-image: -ms-linear-gradient(top, rgba(255,255,255,0.07) 0%,rgba(255,255,255,0) 100%);
    background-image: linear-gradient(to bottom, rgba(255,255,255,0.07) 0%,rgba(255,255,255,0) 100%);
    -webkit-box-shadow: inset rgba(255,255,255,0.1) 0px 1px 0px;
    -moz-box-shadow: inset rgba(255,255,255,0.1) 0px 1px 0px;
    -khtml-box-shadow: inset rgba(255,255,255,0.1) 0px 1px 0px;
    box-shadow: inset rgba(255,255,255,0.1) 0px 1px 0px;
    height: 40px;
    
    
                         
    font-family: Tahoma, Helvetica, Arial, sans-serif;
    color: rgb /*[[Color3]]*/ !important;
    word-wrap: break-word;
    line-height: 1.28;                                 
                                            
                                            
                                            
}
}
