css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("vk.com")
{
/*шапка сайта чуть изменена*/

/*закруглить шапку*/
/*[[opt]]*/
/*шапка сайта чуть изменена*/

/*левое меню*/
/*меню - иконки*/
/*[[color_icon]]*/

/*левое меню*/

/*фон страницы*/
body {background: /*[[body_color]]*/}

.page_block {
    box-shadow: 0 1px 0 0 /*[[page_block_sw_bottom]]*/, 0 0 0 1px /*[[page_block_sw]]*/;
}

.wall_module .copy_quote, .ui_rmenu_item_sel, .ui_rmenu_item_sel:hover, .wall_module .media_desc .lnk.lnk_mail {
    border-left: 2px solid /*[[page_block_sw]]*/;
}

/*Цвет #FFF меняем на /*[[fon_block]]*/ */    
.ui_actions_menu, .page_block {
    background: /*[[fon_block]]*/;
}
   
.ui_search {
    background-color: /*[[fon_block]]*/;
}
  
/*цвет - #e7e8ec бордер, сепаратор, разделитель меняем на /*[[border_block_color]]*/ */       
.page_top, .group_list_row, .ui_tabs, .bt_header, .ui_search, .pv_author_block, .page_block_header,
.blst_row {
    border-bottom: 1px solid /*[[border_block_color]]*/;
}

.post_full_like_wrap, .ui_rmenu_sep, .wall_module .replies_list,
.nim-dialog .nim-dialog--content, .submit_post, .wide_column .topics_module .topic_row, 
.ui_rmenu_sep, .friends_user_row, .docs_item, .counts_module, .wk_table td, .pv_comments, 
.wall_module .reply~.reply .dld, .wall_module .reply~.reply .reply_wrap, .profile_info_block, .search_row {
    border-top: 1px solid /*[[border_block_color]]*/;
}

.wk_table td {
    border-right: 1px solid /*[[border_block_color]]*/;
}

.wk_table, .ui_actions_menu {
    border: 1px solid /*[[border_block_color]]*/;
}

.module, .bp_post {
    border: solid /*[[border_block_color]]*/;
}                           
    
/*бордер в меню - немного другой цвет #dfe2e8..меняем на /*[[sep_menu_color]]*/ */
    
.ui_actions_menu_sep, #side_bar .more_div, .top_profile_sep {
    border-top: 1px solid /*[[sep_menu_color]]*/;
}

.yap-R-A-132169-18 .yap-vk-new-main a.yap-vk-new-item, .yap-R-A-132169-18 .yap-vk-new-main a.yap-vk-new-item:hover, .yap-R-A-132169-18 .yap-vk-new-main a.yap-vk-new-item:visited {
    border-bottom: 1px solid /*[[sep_menu_color]]*/!important;
}
  
.ads_ad_box.redesign .ads_ad_box_border {
    border-color: /*[[sep_menu_color]]*/;
}

/*Прочие бордеры, сепараторы, разделители без изменений*/    
.wk_table {
    border-right:0; 
    border-top: 0;   
}    
    
.module {
    border-width: 1px 0 0;
}

.bp_post{
    border-width: 1px 0;
}

/*менюшка*/
/*фон пунктов меню и цвет текста*/
#side_bar ol li .left_row {
    color: /*[[side_bar_text]]*/;
}

/*подсветка пунктов меню*/
#side_bar ol li .left_row:hover {
         background: /*[[side_bar_hover]]*/;
         color: /*[[side_bar_text_hover]]*/;
} 

/*размер шрифта пунктов меню*/
#side_bar .left_label {
         font-size: /*[[font_size_side_bar]]*/px;
}

/*фон меню*/
/*[[side_bar_fon]]*/  

/*цифры у пунктов, задаем цвет и фон*/
.left_count_wrap, .left_count_wrap.left_count_wrap_hovered {
    color: /*[[left_count_wrap_text]]*/;
    background: /*[[left_count_wrap]]*/;
}

/*Фон у цифры где пункт Мои Друзья*/
.left_count_wrap.left_count_wrap_hovered:hover {
    background-color: /*[[left_count_wrp_hover]]*/;
}

/*всплывающие диалоги*/
.ms_items_more, .eltt, .ui_actions_menu, #top_profile_menu {
    background: /*[[fon_dialog]]*/;
    border: 1px solid /*[[page_block_sw]]*/;
}	

.media_selector .ms_items_more .ms_item:hover, .ui_actions_menu_item:hover, .top_profile_mrow:hover {
    background-color: /*[[hover_fon_dialog]]*/;
}

/*треугольник*/
.ms_items_more_wrap.to_up .ms_items_more:before {
    border-width: 6px;
    margin: 0 -6px;
    border-top-color: /*[[page_block_sw]]*/;
}

.ms_items_more_wrap.to_down .ms_items_more::before, .groups_actions_menu .ui_actions_menu:before, .ui_actions_menu:before, #top_profile_menu:before {
    border-width: 6px;
    margin: 0 -6px;
    border-bottom-color: /*[[page_block_sw]]*/;
}

.ms_items_more_wrap.to_up .ms_items_more:after {
    border-width: 5px;
    margin: 0 -5px;
    border-top-color: /*[[fon_dialog]]*/;
}

.ms_items_more_wrap.to_down .ms_items_more::after, .groups_actions_menu .ui_actions_menu:after, .ui_actions_menu:after, #top_profile_menu:after {
    border-width: 5px;
    margin: 0 -5px;
    border-bottom-color: /*[[fon_dialog]]*/;
} 

.eltt.eltt_top:before {
    border-top-color: /*[[page_block_sw]]*/;
    margin-left: -6px;
}

.eltt.eltt_top:after {
    border-top-color: /*[[fon_dialog]]*/;
    margin-left: -5px;
}

/*текст при наведении в пунктах блоков - цвет меняем #000 - пока тестовый вариант*/
.ui_rmenu_item_sel, .ui_rmenu_item_sel:hover {
    color: /*[[a_link]]*/;
}

/*текст ссылок - цвет меняем #2a5885*/
.audio_row.lyrics .audio_title_inner, .topics_module .topic_inner_link, a, .ui_rmenu_item, .ui_rmenu_subitem, a.group_row_title, a.reply_link._reply_lnk, .profile_info .labeled > a, .post_reply_tt_choose>.radiobtn, .ui_actions_menu_item, .top_profile_mrow {
    color: /*[[a_link]]*/;
}
  
/*серый текст #939393 на другой*/ 
.yap-R-A-132169-18 .yap-vk-new-domain, .yap-R-A-132169-18 .yap-vk-new-warning, .yap-R-A-132169-18 .yap-vk-new-logo, .yap-R-A-132169-18 .yap-vk-new-logo *{
    color: /*[[text1_col]]*/!important;
} 
.ts_contact_info, .audio_friend_audios_count, .audio_friend_now, .post_header_info .explain, .post_author, .pg_in, a.bp_date, .audio_row .audio_duration_wrap, .no_current_info, h4.profile_online, .left_menu_nav, .ui_tab_count, .right_list_field, .wall_module .reply_to, .wd_lnk, .post_date, .post_date .post_link, .post_date .wall_text_name_explain_promoted_post, .post_video_views_count, .header_right_link, .module_header .header_count {
    color: /*[[text1_col]]*/;
}

/*серый текст #828282 на другой*/
.group_info .label, .page_counter .label, .profile_info .label, h3 {
    color: /*[[text2_col]]*/;
}

/*серый текст #656565 на другой*/ 
.friends_common_label, .friends_field, .wide_column .topics_module .topic_info, .group_row_info, .page_list_module .group_desc, .page_list_module .people_desc, .page_list_module .people_extra, .ui_tab, .ui_tabs .ui_tab {
    color: /*[[text3_col]]*/;}

/*синий текст #42648b на другой*/ 
.yap-R-A-132169-18 .yap-vk-new-title{
    color: /*[[text4_col]]*/!important;
}
.wall_copy_more, .wall_post_more, .wall_reply_more, .bp_author, .wide_column .topics_module .topic_title, .page_counter .count, .page_counter:hover .label {
    color: /*[[text4_col]]*/;
}

/*подсветка пунктов в отдельных блоках*/
.no_current_info:hover, .group_wiki_hider:hover, .audio_row:not(.audio_row_current):hover, .profile_more_info_link:hover, .ui_rmenu_item:hover, .ui_rmenu_subitem:hover, .ui_rmenu_item_sel, .ui_rmenu_item_sel:hover {
    background-color: /*[[fon_block_hover]]*/!important;}

.my_current_info:hover {
    background-color: /*[[fon_dialog]]*/!important;}

/*подсветка кнопок #dfe6ed на другой*/
.button_gray button.hover, .button_gray button:hover, .flat_button.secondary.hover, .flat_button.secondary:hover {
    background-color: /*[[fon_button_hover]]*/;}
}
