css:
@-moz-document domain("csgolounge.com") {
/**created by Seishiin**/

/*Main*/
body
{   background: #373737;}
.title
{   color: rgba(242,138,23,0.8);
    background-image: none!important;
    padding-left: 4px!important;}
header
{   background: rgba(255, 255, 255,0.1);
    box-shadow: none;}
aside
{   background: transparent;}
div#ajaxCont
{   color: /*[[FontColor]]*/;}

/*Matches*/
.matchheader
{   background: transparent;
    padding: 10px;
    border-color: rgba(255, 255, 255,0.1);}
.match
{   box-shadow: none;}
.whenm, .eventm
{   text-shadow: none;
    color: /*[[FontColor]]*/;
    margin-top: 4px;
    margin-bottom: 4px;
    margin-right: 1px;}
.teamtext
{   padding-top: 3px;
	background:transparent;}

/*Trades*/
.tradeheader, .bpheader
{   background: transparent;
    border-color: rgba(255,255,255,0.1);
    color: rgba(255,255,255,0.5);}
.tradeheader > a[href^="trade"]
{   color: /*[[FontColor]]*/;
    margin-top: 4px;
    margin-bottom: 4px;
    margin-right: 1px;
    text-shadow: none!important;}
.tradecnt > .left
{   background: #4b4b4b;
    box-shadow: 0px 0px 3px 2px rgba(75,75,75,1);}
.tradecnt > .right
{   background: #4b4b4b;
    box-shadow: 0px 0px 3px 2px rgba(75,75,75,1);}

/*Messages*/
.message
{   color: /*[[FontColor]]*/;
    text-shadow: none!important;
    background: #4b4b4b;
    box-shadow: 0px 0px 3px 2px rgba(75,75,75,1);}
.msgleft > b > .user
{   color: /*[[FontColor]]*/;}
.standard
{   color: /*[[FontColor]]*/;
    text-shadow: none!important;}

/*Donators*/
.donor
{   color: #74b0ed;
    text-shadow: none!important;}
.donor2
{   color: #4c6aff;
    text-shadow: none!important;}
.donor3
{   color:#8545fd;
    text-shadow: none!important;}
.donor4
{   color:#f7dc18;
    text-shadow: none!important;}
	
/*FontColor*/
.title
{color:/*[[FontColor]]*/;}
.box-shiny a
{   color: /*[[FontColor]]*/;}
#mt > div
{   color: /*[[FontColor]]*/;}
.box-shiny a, .box-shiny-alt a
{   color: /*[[FontColor]]*/;}
nav a[href="http://www.reddit.com/r/csgolounge"]
{   color: /*[[FontColor]]*/;}
nav a[href="/premium"]
{   color: /*[[FontColor]]*/;}
nav a[href="guide"]
{   color: /*[[FontColor]]*/;}
nav a[href="rules"]
{   color: /*[[FontColor]]*/;}
nav a[href="/contact"]
{   color: /*[[FontColor]]*/;}
nav a[href="http://steamcommunity.com/groups/csgolounge/discussions"]
{   color: /*[[FontColor]]*/!important;}
a#mlog
{   color: /*[[FontColor]]*/;}
#menu a
{   color: /*[[FontColor]]*/;}
.info
{background:/*[[FontColor]]*/;}
a[href="/trades"]
{   color: /*[[FontColor]]*/;
    text-shadow: none!important;}

/*Hover*/
a:hover > .user
{   color: /*[[Hover]]*/;}
a:hover > .donor
{   color: /*[[Hover]]*/;}
a:hover > .donor2
{   color: /*[[Hover]]*/;}
a:hover > .donor3
{   color: /*[[Hover]]*/;}
a:hover > .donor4
{   color: /*[[Hover]]*/;}
a:hover[href^="http://steamcommunity.com/linkfilter"]
{   color: /*[[Hover]]*/;}
a:hover[href^="http://steamcommunity.com"]
{   color: /*[[Hover]]*/;}
a:hover[href="/donations"]
{   color: /*[[Hover]]*/;}
.tradeheader a:hover
{   color: /*[[Hover]]*/;}
div#backpack > div.standard > a:hover
{	color: /*[[Hover]]*/;}

/*Links*/
a[href^="http://steamcommunity.com"]
{   color: /*[[FontColor]]*/;}
a[href^="http://steamcommunity.com/linkfilter"]
{   color: /*[[FontColor]]*/;
    text-shadow: none!important;}
a[href="/trades"]
{   color: /*[[FontColor]]*/;
    text-shadow: none!important;}

/*Backpack*/
div#backpack > div.standard > a
{   text-shadow: none!important;
    color: /*[[FontColor]]*/;}
#backpack
{	color:/*[[FontColor]]*/;}
.srch
{color:/*[[FontColor]]*/;}
.tab:hover
{	color: /*[[Hover]]*/!important;}
div.title a:hover
{color:/*[[Hover]]*/;}

/*[[Rarity]]*/

/*Submenu*/
div#g2aitem,article.standard #betreturns .bpheader,aside#submenu a img, .gwd-page-container
{   display: none !important;}
#follow
{	display:none;}
footer
{	display:none;}
aside .ads
{	display:none;}
  
/*Disqus*/
div#disqus_thread
{	background: #4b4b4b!important;
	box-shadow: 0px 0px 0px 10px rgba(75,75,75,1);
  border-radius: 15px;}
}
